import React, { useState } from 'react';
import { X, Edit2, Check } from 'lucide-react';
import QuestionModal from './QuestionModal';

const CategoryDetailModal = ({ category, categoryKey, allQuestions, answeredQuestions, assessmentId, onClose, onUpdate }) => {
  const [selectedQuestion, setSelectedQuestion] = useState(null);

  // Get questions for this category
  const categoryQuestions = allQuestions.filter(q => q.category === categoryKey);

  // Organize by subject
  const questionsBySubject = {};
  categoryQuestions.forEach(q => {
    if (!questionsBySubject[q.subject]) {
      questionsBySubject[q.subject] = [];
    }
    questionsBySubject[q.subject].push(q);
  });

  // Get answer for a question
  const getAnswer = (questionId) => {
    return answeredQuestions.find(a => a.question_id === questionId);
  };

  // Calculate score (0-5 to percentage)
  const getScorePercent = (answerValue) => {
    if (answerValue === null || answerValue === undefined) return 0;
    return (answerValue / 5) * 100;
  };

  const getScoreColor = (percent) => {
    if (percent < 30) return 'bg-red-500';
    if (percent < 60) return 'bg-yellow-500';
    return 'bg-green-500';
  };

  const handleQuestionClick = (question) => {
    const answer = getAnswer(question.question_id);
    setSelectedQuestion({
      ...question,
      category: categoryKey,
      subject: question.subject,
      answer_value: answer?.answer_value,
      answer_text: answer?.answer_text,
      comments: answer?.comments,
      id: answer?.id
    });
  };

  const handleSaveResponse = async () => {
    setSelectedQuestion(null);
    if (onUpdate) {
      await onUpdate();
    }
  };

  const answeredCount = categoryQuestions.filter(q => getAnswer(q.question_id)).length;
  const totalCount = categoryQuestions.length;
  const categoryScore = category.score || 0;

  return (
    <>
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-40 p-4">
        <div className="bg-white rounded-lg shadow-2xl w-full max-w-5xl max-h-[90vh] flex flex-col">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-gray-200 bg-gray-50">
            <div className="flex-1">
              <h2 className="text-2xl font-bold text-gray-900">{category.name}</h2>
              <p className="text-sm text-gray-600 mt-1">
                {answeredCount} of {totalCount} questions answered • {Math.round(categoryScore)}% score
              </p>
            </div>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 transition ml-4"
            >
              <X size={28} />
            </button>
          </div>

          {/* Category Score Bar */}
          <div className="px-6 py-4 bg-blue-50 border-b border-blue-100">
            <div className="flex items-center justify-between mb-2">
              <span className="text-sm font-semibold text-gray-700">Category Score</span>
              <span className="text-sm font-semibold text-gray-900">{Math.round(categoryScore)}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-4">
              <div
                className={`h-4 rounded-full transition-all ${getScoreColor(categoryScore)}`}
                style={{ width: `${categoryScore}%` }}
              ></div>
            </div>
          </div>

          {/* Content - Scrollable */}
          <div className="flex-1 overflow-y-auto p-6">
            <div className="space-y-6">
              {Object.keys(questionsBySubject).map((subjectName) => {
                const questions = questionsBySubject[subjectName];
                const subjectAnswered = questions.filter(q => getAnswer(q.question_id)).length;

                // Calculate subject average score
                const subjectScores = questions
                  .map(q => getAnswer(q.question_id)?.answer_value)
                  .filter(v => v !== null && v !== undefined);
                const subjectAvgScore = subjectScores.length > 0
                  ? (subjectScores.reduce((a, b) => a + b, 0) / subjectScores.length / 5) * 100
                  : 0;

                return (
                  <div key={subjectName} className="border border-gray-200 rounded-lg overflow-hidden">
                    {/* Subject Header */}
                    <div className="bg-gray-50 p-4 border-b border-gray-200">
                      <div className="flex items-center justify-between mb-3">
                        <h3 className="font-bold text-lg text-gray-900">{subjectName}</h3>
                        <span className="text-sm font-semibold text-gray-600">
                          {subjectAnswered} / {questions.length}
                        </span>
                      </div>
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div
                          className={`h-2 rounded-full transition-all ${getScoreColor(subjectAvgScore)}`}
                          style={{ width: `${subjectAvgScore}%` }}
                        ></div>
                      </div>
                    </div>

                    {/* Questions */}
                    <div className="p-4 space-y-3">
                      {questions.map((question) => {
                        const answer = getAnswer(question.question_id);
                        const isAnswered = answer !== undefined;
                        const scorePercent = getScorePercent(answer?.answer_value);

                        return (
                          <div
                            key={question.question_id}
                            onClick={() => handleQuestionClick(question)}
                            className={`p-4 rounded-lg border-2 cursor-pointer transition hover:shadow-md ${
                              isAnswered
                                ? 'border-green-200 bg-green-50 hover:border-green-300'
                                : 'border-gray-200 bg-white hover:border-blue-300'
                            }`}
                          >
                            {/* Question Text */}
                            <div className="flex items-start justify-between mb-3">
                              <p className="text-sm font-medium text-gray-900 flex-1 pr-4">
                                {question.question_text}
                              </p>
                              <Edit2 size={16} className="text-gray-400 flex-shrink-0" />
                            </div>

                            {/* Progress Bar */}
                            {isAnswered ? (
                              <>
                                <div className="flex items-center justify-between mb-2">
                                  <span className="text-xs text-gray-600">Your Score</span>
                                  <span className="text-xs font-semibold text-gray-900">
                                    {Math.round(scorePercent)}%
                                  </span>
                                </div>
                                <div className="w-full bg-gray-200 rounded-full h-2 mb-3">
                                  <div
                                    className={`h-2 rounded-full transition-all ${getScoreColor(scorePercent)}`}
                                    style={{ width: `${scorePercent}%` }}
                                  ></div>
                                </div>
                                <div className="flex items-center space-x-2">
                                  <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                    <Check size={12} className="mr-1" />
                                    {answer.answer_text}
                                  </span>
                                </div>
                              </>
                            ) : (
                              <div className="flex items-center justify-between">
                                <div className="w-full bg-gray-200 rounded-full h-2">
                                  <div className="h-2 rounded-full bg-gray-300" style={{ width: '0%' }}></div>
                                </div>
                                <span className="text-xs text-gray-500 ml-3 whitespace-nowrap">
                                  Not answered
                                </span>
                              </div>
                            )}
                          </div>
                        );
                      })}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Footer */}
          <div className="flex items-center justify-between p-6 border-t border-gray-200 bg-gray-50">
            <div className="text-sm text-gray-600">
              Click any question to view or edit your answer
            </div>
            <button
              onClick={onClose}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 transition"
            >
              Close
            </button>
          </div>
        </div>
      </div>

      {/* Question Edit Modal */}
      {selectedQuestion && (
        <QuestionModal
          question={selectedQuestion}
          assessmentId={assessmentId}
          onClose={() => setSelectedQuestion(null)}
          onSave={handleSaveResponse}
        />
      )}
    </>
  );
};

export default CategoryDetailModal;
