// Enhanced AdvancedValuationModal.jsx with Industry Selection & Private Discount
// Replace your existing AdvancedValuationModal component with this

import React, { useState, useEffect } from 'react';
import { X, TrendingUp, DollarSign, BarChart3, Percent } from 'lucide-react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const AdvancedValuationModal = ({ isOpen, onClose, onSubmit }) => {
  const [currentYear] = useState(new Date().getFullYear());
  const years = [currentYear - 3, currentYear - 2, currentYear - 1, currentYear];

  // NEW: Industry and discount states
  const [industries, setIndustries] = useState([]);
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [privateDiscount, setPrivateDiscount] = useState(25);

  const [financialData, setFinancialData] = useState({
    revenue: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    gross_profit: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    ebitda: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    depreciation: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    ebit: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    profit_after_tax: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    addbacks_total: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    sde: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    cash: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    current_assets: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    non_current_assets: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    total_assets: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    long_term_debt: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    short_term_debt: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    interest_bearing_debt: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    equity: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    total_liabilities_equity: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    owner_salary: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    family_salaries: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    salary_adjustments: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    auto_expenses: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    donations: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    insurance_premiums: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    inventory_adjustments: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    meals_entertainment: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    personal_expenses: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    phone_internet: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    non_business_services: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    travel: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    one_time_charges: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    retirement: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    supplier_rebates: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
    other_addbacks: { [years[0]]: '', [years[1]]: '', [years[2]]: '', [years[3]]: '' },
  });

  const [activeTab, setActiveTab] = useState('settings'); // Start with settings tab

  // NEW: Fetch industries on mount
  useEffect(() => {
    if (isOpen) {
      fetchIndustries();
    }
  }, [isOpen]);

  const fetchIndustries = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/valuation/industries', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setIndustries(response.data.industries);
    } catch (err) {
      console.error('Error fetching industries:', err);
    }
  };

  const handleInputChange = (field, year, value) => {
    setFinancialData(prev => ({
      ...prev,
      [field]: { ...prev[field], [year]: value }
    }));
  };

  const formatCurrency = (value) => {
    if (!value) return '$0';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(value);
  };

  const prepareChartData = () => {
    return years.map(year => ({
      year: year.toString(),
      'Gross Profit': parseFloat(financialData.gross_profit[year]) || 0,
      'EBITDA': parseFloat(financialData.ebitda[year]) || 0,
      'SDE': parseFloat(financialData.sde[year]) || 0,
    }));
  };

  const handleSubmit = () => {
    // Include industry and discount in submission
    const submissionData = {
      ...financialData,
      industry_id: selectedIndustry,
      private_company_discount: privateDiscount / 100
    };
    onSubmit(submissionData);
  };

  if (!isOpen) return null;

  const chartData = prepareChartData();
  const selectedIndustryData = industries.find(ind => ind.id === parseInt(selectedIndustry));

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg w-full max-w-7xl max-h-[95vh] overflow-hidden flex flex-col">
        <div className="p-6 border-b border-gray-200 flex justify-between items-center bg-gradient-to-r from-blue-600 to-indigo-600">
          <div className="flex items-center gap-3">
            <BarChart3 className="text-white" size={28} />
            <div>
              <h2 className="text-2xl font-bold text-white">Advanced Financial Analysis</h2>
              <p className="text-blue-100 text-sm">Multi-year detailed valuation</p>
            </div>
          </div>
          <button onClick={onClose} className="text-white hover:bg-white/20 p-2 rounded-lg">
            <X size={24} />
          </button>
        </div>

        {/* Tab Navigation */}
        <div className="flex border-b border-gray-200 bg-gray-50">
          {['settings', 'income', 'balance', 'addbacks', 'charts'].map(tab => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`flex-1 px-6 py-4 font-semibold transition-colors ${
                activeTab === tab
                  ? 'bg-white text-blue-600 border-b-2 border-blue-600'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              {tab === 'settings' && '⚙️ Settings'}
              {tab === 'income' && '📊 Income'}
              {tab === 'balance' && '🏦 Balance'}
              {tab === 'addbacks' && '➕ Add-backs'}
              {tab === 'charts' && '📈 Charts'}
            </button>
          ))}
        </div>

        <div className="flex-1 overflow-y-auto p-6">
          {/* NEW: Settings Tab */}
          {activeTab === 'settings' && (
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Valuation Settings</h3>

              {/* Industry Selection */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Industry *
                </label>
                <select
                  value={selectedIndustry}
                  onChange={(e) => setSelectedIndustry(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500"
                  required
                >
                  <option value="">Select your industry...</option>
                  {industries.map(industry => (
                    <option key={industry.id} value={industry.id}>
                      {industry.industry_name}
                    </option>
                  ))}
                </select>
                {selectedIndustryData && (
                  <div className="mt-2 p-3 bg-blue-50 rounded-lg text-sm">
                    <p className="font-medium text-blue-900">Industry Multiples (Public Companies):</p>
                    <p className="text-blue-700">
                      EV/EBITDA: {selectedIndustryData.ev_ebitda.median}x | 
                      EV/Revenue: {selectedIndustryData.ev_revenue.median}x | 
                      P/E: {selectedIndustryData.pe.median}x
                    </p>
                  </div>
                )}
              </div>

              {/* Private Company Discount Slider */}
              <div className="mb-6 p-4 bg-gradient-to-r from-purple-50 to-pink-50 border border-purple-200 rounded-lg">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center gap-2">
                    <Percent className="text-purple-600" size={20} />
                    <label className="text-sm font-semibold text-gray-800">
                      Private Company Discount
                    </label>
                  </div>
                  <span className="text-2xl font-bold text-purple-600">
                    {privateDiscount}%
                  </span>
                </div>

                <input
                  type="range"
                  min="0"
                  max="50"
                  step="5"
                  value={privateDiscount}
                  onChange={(e) => setPrivateDiscount(parseInt(e.target.value))}
                  className="w-full h-3 bg-gradient-to-r from-purple-200 to-pink-200 rounded-lg appearance-none cursor-pointer"
                  style={{
                    background: `linear-gradient(to right, #9333ea 0%, #9333ea ${privateDiscount * 2}%, #e9d5ff ${privateDiscount * 2}%, #e9d5ff 100%)`
                  }}
                />

                <div className="flex justify-between text-xs text-gray-600 mt-2">
                  <span>0% (No discount)</span>
                  <span>25% (Standard)</span>
                  <span>50% (Maximum)</span>
                </div>

                {selectedIndustryData && (
                  <div className="mt-3 pt-3 border-t border-purple-200">
                    <p className="text-xs font-medium text-gray-700 mb-1">Adjusted Multiples:</p>
                    <p className="text-sm text-purple-700 font-semibold">
                      EV/EBITDA: {(selectedIndustryData.ev_ebitda.median * (1 - privateDiscount / 100)).toFixed(1)}x | 
                      EV/Revenue: {(selectedIndustryData.ev_revenue.median * (1 - privateDiscount / 100)).toFixed(1)}x | 
                      P/E: {(selectedIndustryData.pe.median * (1 - privateDiscount / 100)).toFixed(1)}x
                    </p>
                  </div>
                )}
              </div>

              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <h4 className="font-semibold text-blue-900 mb-2">💡 Next Steps</h4>
                <ol className="text-sm text-blue-800 space-y-1">
                  <li>1. Select your industry above</li>
                  <li>2. Adjust the private company discount if needed</li>
                  <li>3. Navigate to "Income", "Balance", and "Add-backs" tabs to enter financial data</li>
                  <li>4. View "Charts" tab to see trends</li>
                  <li>5. Click "Calculate Advanced Valuation" when ready</li>
                </ol>
              </div>
            </div>
          )}

          {/* Income Statement Tab - KEEP EXISTING CODE */}
          {activeTab === 'income' && (
            <div className="overflow-x-auto">
              <table className="w-full border-collapse">
                <thead>
                  <tr className="bg-gray-100">
                    <th className="text-left p-3 border">Line Item</th>
                    {years.map(y => <th key={y} className="p-3 border">{y}</th>)}
                  </tr>
                </thead>
                <tbody>
                  {[
                    { label: 'Revenue', field: 'revenue' },
                    { label: 'Gross Profit', field: 'gross_profit' },
                    { label: 'EBITDA', field: 'ebitda' },
                    { label: 'Depreciation', field: 'depreciation' },
                    { label: 'EBIT', field: 'ebit' },
                    { label: 'Profit After Tax', field: 'profit_after_tax' },
                    { label: 'Add-backs Total', field: 'addbacks_total' },
                    { label: 'SDE', field: 'sde' },
                  ].map((item, idx) => (
                    <tr key={item.field} className={idx % 2 === 0 ? 'bg-white' : 'bg-gray-50'}>
                      <td className="p-3 border font-medium">{item.label}</td>
                      {years.map(year => (
                        <td key={year} className="p-2 border">
                          <input
                            type="number"
                            value={financialData[item.field][year]}
                            onChange={(e) => handleInputChange(item.field, year, e.target.value)}
                            className="w-full px-2 py-1 border rounded"
                            placeholder="$0"
                          />
                        </td>
                      ))}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Keep all other existing tabs (balance, addbacks, charts) as they are */}
          {/* ... */}
        </div>

        <div className="p-6 border-t bg-gray-50 flex justify-between items-center">
          <button
            onClick={onClose}
            className="px-6 py-2 border rounded-lg hover:bg-gray-100"
          >
            Cancel
          </button>
          <div className="flex items-center gap-4">
            {!selectedIndustry && (
              <span className="text-sm text-red-600">⚠️ Please select an industry</span>
            )}
            <button
              onClick={handleSubmit}
              disabled={!selectedIndustry}
              className={`px-6 py-3 rounded-lg flex items-center gap-2 ${
                selectedIndustry
                  ? 'bg-blue-600 text-white hover:bg-blue-700'
                  : 'bg-gray-300 text-gray-500 cursor-not-allowed'
              }`}
            >
              <BarChart3 size={20} />
              Calculate Advanced Valuation
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdvancedValuationModal;
