import React, { useState, useEffect } from 'react';
import { FileText, PlayCircle, ChevronDown, ChevronRight, Edit2, Check } from 'lucide-react';
import { getAssessment, startAssessment } from '../services/api';
import AssessmentModal from '../components/AssessmentModal';
import QuestionModal from '../components/QuestionModal';
import './GapAnalysis.css';

const GapAnalysis = () => {
  const [assessment, setAssessment] = useState(null);
  const [allQuestions, setAllQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAssessmentModal, setShowAssessmentModal] = useState(false);
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [hoveredGapZone, setHoveredGapZone] = useState(null);
  const [selectedGapFilter, setSelectedGapFilter] = useState(null);

  useEffect(() => {
    loadAssessment();
    loadAllQuestions();
  }, []);

  const loadAssessment = async () => {
    setLoading(true);
    try {
      const result = await getAssessment();

      if (result.success) {
        console.log('Assessment loaded:', result.data);
        setAssessment(result.data);
      } else {
        // No assessment exists, start one
        const startResult = await startAssessment();
        if (startResult.success) {
          const newResult = await getAssessment();
          setAssessment(newResult.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
    setLoading(false);
  };

  const loadAllQuestions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/assessment/questions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setAllQuestions(data.questions || []);
      }
    } catch (error) {
      console.error('Error loading questions:', error);
    }
  };

  const handleModalSave = async () => {
    // Reload assessment to update scores
    await loadAssessment();
  };

  // Get all answered questions from assessment.responses
  const getAnsweredQuestions = () => {
    if (!assessment || !assessment.responses) return [];
    return assessment.responses;
  };

  // Get questions for a specific category
  const getCategoryQuestions = (categoryKey) => {
    return allQuestions.filter(q => q.category === categoryKey);
  };

  // Categorize questions by gap zone
  const getGapZone = (score) => {
    if (score >= 72) return 'no-gaps';
    if (score >= 67) return 'minor-gaps';
    if (score >= 50) return 'considerable-gaps';
    if (score >= 30) return 'critical-gaps';
    return 'very-critical-gaps';
  };

  const getGapZoneInfo = (zone) => {
    const zones = {
      'very-critical-gaps': { label: 'Very Critical Gaps', color: '#dc2626', range: '0-30%' },
      'critical-gaps': { label: 'Critical Gaps', color: '#f97316', range: '30-50%' },
      'considerable-gaps': { label: 'Considerable Gaps', color: '#eab308', range: '50-67%' },
      'minor-gaps': { label: 'Minor Gaps', color: '#84cc16', range: '67-72%' },
      'no-gaps': { label: 'No Gaps', color: '#16a34a', range: '72%+' }
    };
    return zones[zone];
  };

  // Get questions grouped by gap zones
  const getQuestionsByGapZone = () => {
    const answered = getAnsweredQuestions();
    const zones = {
      'very-critical-gaps': [],
      'critical-gaps': [],
      'considerable-gaps': [],
      'minor-gaps': [],
      'no-gaps': []
    };

    answered.forEach(answer => {
      // Use the score directly from the response (already 0-100)
      const zone = getGapZone(answer.score);
      zones[zone].push(answer);
    });

    return zones;
  };

  // Get answer for a question
  const getAnswer = (questionId) => {
    const answered = getAnsweredQuestions();
    return answered.find(a => a.question_id === questionId);
  };

  const getScoreColor = (percent) => {
    if (percent >= 72) return 'bg-green-600';  // Areas without gaps
    if (percent >= 67) return 'bg-lime-500';   // Areas with minor gaps
    if (percent >= 50) return 'bg-yellow-500'; // Areas with considerable gaps
    if (percent >= 30) return 'bg-orange-500'; // Areas with critical gaps
    return 'bg-red-600';                       // Areas with very critical gaps
  };

  const handleQuestionEdit = (question, categoryKey) => {
    const answer = getAnswer(question.question_id);
    setSelectedQuestion({
      ...question,
      category: categoryKey,
      subject: question.subject,
      answer_value: answer?.answer_value,
      answer_text: answer?.answer_text,
      comments: answer?.comments,
      id: answer?.id
    });
  };

  const handleQuestionSave = async () => {
    setSelectedQuestion(null);
    await loadAssessment();
  };

  const handleStartAssessment = async () => {
    setShowAssessmentModal(true);
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-gray-600">Loading assessment...</div>
      </div>
    );
  };

  if (!assessment) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Assessment Found</h2>
          <button
            onClick={async () => {
              await startAssessment();
              loadAssessment();
            }}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Start Assessment
          </button>
        </div>
      </div>
    );
  }

  const answeredQuestions = getAnsweredQuestions();
  const progressPercent = allQuestions.length > 0
    ? (assessment.answered_questions / allQuestions.length) * 100
    : 0;

  // Organize questions by category for display
  const categoryScores = assessment.category_scores || {};
  const categoryKeys = Object.keys(categoryScores);

  return (
    <div className="p-6 max-w-7xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Gap Analysis</h1>
        <p className="text-gray-600">
          Assess your business exit readiness and identify areas for improvement
        </p>
      </div>

      {/* Show "Start Assessment" if no answers yet */}
      {assessment.answered_questions === 0 && (
        <div className="bg-white rounded-lg border-2 border-blue-200 p-8 text-center mb-6">
          <div className="mb-4">
            <FileText size={48} className="mx-auto text-blue-600" />
          </div>
          <h2 className="text-2xl font-bold text-gray-900 mb-2">
            Ready to assess your exit readiness?
          </h2>
          <p className="text-gray-600 mb-6">
            Complete our comprehensive 89-question assessment to identify gaps and opportunities
          </p>
          <button
            onClick={handleStartAssessment}
            className="inline-flex items-center px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 font-semibold transition"
          >
            <PlayCircle size={20} className="mr-2" />
            Start Assessment
          </button>
        </div>
      )}

      {/* Show Dashboard if assessment has answers */}
      {assessment.answered_questions > 0 && (
        <div className="space-y-6">
          {/* Overall Progress Card */}
          <div className="bg-gradient-to-r from-blue-500 to-blue-600 rounded-lg p-6 text-white">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h2 className="text-2xl font-bold">Overall Exit Readiness Score</h2>
                <p className="text-blue-100 mt-1">
                  Based on {assessment.answered_questions} of {allQuestions.length} questions answered
                </p>
              </div>
              <div className="text-right">
                <div className="text-5xl font-bold">{Math.round(assessment.overall_score)}%</div>
                <div className="text-blue-100 text-sm mt-1">
                  {assessment.overall_score >= 72 ? 'Excellent' :
                   assessment.overall_score >= 67 ? 'Good' :
                   assessment.overall_score >= 50 ? 'Fair' :
                   assessment.overall_score >= 30 ? 'Needs Work' : 'Critical'}
                </div>
              </div>
            </div>

            {/* Progress Bar */}
            <div className="bg-white/20 rounded-full h-3 overflow-hidden">
              <div
                className="bg-white h-full transition-all duration-500 rounded-full"
                style={{ width: `${progressPercent}%` }}
              ></div>
            </div>
            <div className="mt-2 flex justify-between text-sm text-blue-100">
              <span>{Math.round(progressPercent)}% Complete</span>
              <span>{allQuestions.length - assessment.answered_questions} questions remaining</span>
            </div>

            {assessment.answered_questions < allQuestions.length && (
              <div className="mt-4">
                <button
                  onClick={handleStartAssessment}
                  className="px-6 py-2 bg-white text-blue-600 rounded-lg hover:bg-blue-50 font-semibold transition"
                >
                  Continue Assessment
                </button>
              </div>
            )}
          </div>

          {/* Category Scores Grid */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-xl font-bold text-gray-900 mb-4">Category Scores</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {categoryKeys.map((catKey) => {
                const score = categoryScores[catKey];
                const categoryName = catKey.split('_').map(word => 
                  word.charAt(0).toUpperCase() + word.slice(1)
                ).join(' ').replace(' Score', '');

                return (
                  <div key={catKey} className="border border-gray-200 rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-semibold text-gray-900">{categoryName}</h4>
                      <span className={`text-lg font-bold ${
                        score >= 72 ? 'text-green-600' :
                        score >= 67 ? 'text-lime-600' :
                        score >= 50 ? 'text-yellow-600' :
                        score >= 30 ? 'text-orange-600' : 'text-red-600'
                      }`}>
                        {Math.round(score)}%
                      </span>
                    </div>
                    <div className="w-full bg-gray-200 rounded-full h-2">
                      <div
                        className={`h-full rounded-full transition-all duration-500 ${getScoreColor(score)}`}
                        style={{ width: `${score}%` }}
                      ></div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Charts Row */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {/* Gap Distribution Pie Chart */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Gap Distribution
              </h3>
              <div className="relative">
                {answeredQuestions.length > 0 ? (
                  <div className="relative w-full h-64 flex items-center justify-center">
                    <svg viewBox="0 0 200 200" className="w-64 h-64">
                      {(() => {
                        const questionsByZone = getQuestionsByGapZone();
                        let currentAngle = -90;

                        return Object.keys(questionsByZone).map((zone) => {
                          const count = questionsByZone[zone].length;
                          if (count === 0) return null;

                          const zoneInfo = getGapZoneInfo(zone);
                          const sweepAngle = (count / answeredQuestions.length) * 360;

                          // Calculate path
                          const startAngle = currentAngle;
                          const endAngle = currentAngle + sweepAngle;
                          const isHovered = hoveredGapZone === zone;
                          const isSelected = selectedGapFilter === zone;
                          const radius = isHovered || isSelected ? 85 : 80;

                          const largeArc = sweepAngle > 180 ? 1 : 0;
                          const startX = 100 + radius * Math.cos((startAngle * Math.PI) / 180);
                          const startY = 100 + radius * Math.sin((startAngle * Math.PI) / 180);
                          const endX = 100 + radius * Math.cos((endAngle * Math.PI) / 180);
                          const endY = 100 + radius * Math.sin((endAngle * Math.PI) / 180);

                          currentAngle = endAngle;

                          return (
                            <g key={zone}>
                              <path
                                d={`M 100 100 L ${startX} ${startY} A ${radius} ${radius} 0 ${largeArc} 1 ${endX} ${endY} Z`}
                                fill={zoneInfo.color}
                                opacity={isHovered || isSelected ? 1 : 0.8}
                                onMouseEnter={() => setHoveredGapZone(zone)}
                                onMouseLeave={() => setHoveredGapZone(null)}
                                onClick={() => setSelectedGapFilter(selectedGapFilter === zone ? null : zone)}
                                className="cursor-pointer transition-all duration-200"
                                style={{ transformOrigin: '100px 100px' }}
                              />
                            </g>
                          );
                        });
                      })()}

                      {/* Center circle */}
                      <circle cx="100" cy="100" r="50" fill="white" />
                    </svg>

                    {/* Center text */}
                    <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                      <div className="text-center">
                        {hoveredGapZone ? (
                          <>
                            <div className="text-3xl font-bold" style={{ color: getGapZoneInfo(hoveredGapZone).color }}>
                              {getQuestionsByGapZone()[hoveredGapZone].length}
                            </div>
                            <div className="text-xs text-gray-600 mt-1">
                              {getGapZoneInfo(hoveredGapZone).label}
                            </div>
                          </>
                        ) : (
                          <>
                            <div className="text-4xl font-bold text-blue-900">
                              {Math.round(assessment.overall_score)}
                            </div>
                            <div className="text-xs text-gray-600">Score</div>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <div className="text-gray-400 mb-2">
                      <svg className="w-24 h-24 mx-auto" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <p className="text-gray-600">No data yet</p>
                    <p className="text-sm text-gray-500">Answer questions to see distribution</p>
                  </div>
                )}
              </div>

              {/* Legend */}
              {assessment.answered_questions > 0 && (
                <div className="mt-6 space-y-2">
                  {(() => {
                    const questionsByZone = getQuestionsByGapZone();
                    const zones = ['no-gaps', 'minor-gaps', 'considerable-gaps', 'critical-gaps', 'very-critical-gaps'];

                    return zones.map((zone) => {
                      const count = questionsByZone[zone].length;
                      if (count === 0) return null;

                      const zoneInfo = getGapZoneInfo(zone);
                      const isHovered = hoveredGapZone === zone;
                      const isSelected = selectedGapFilter === zone;

                      return (
                        <div
                          key={zone}
                          className={`flex items-center justify-between p-2 rounded cursor-pointer transition ${
                            isHovered || isSelected ? 'bg-gray-100' : 'hover:bg-gray-50'
                          }`}
                          onMouseEnter={() => setHoveredGapZone(zone)}
                          onMouseLeave={() => setHoveredGapZone(null)}
                          onClick={() => setSelectedGapFilter(selectedGapFilter === zone ? null : zone)}
                        >
                          <div className="flex items-center space-x-2">
                            <div
                              className="w-3 h-3 rounded"
                              style={{ backgroundColor: zoneInfo.color }}
                            ></div>
                            <span className="text-sm text-gray-700">
                              {zoneInfo.label} ({zoneInfo.range})
                            </span>
                          </div>
                          <span className={`text-sm font-semibold ${isSelected ? 'text-blue-600' : 'text-gray-900'}`}>
                            {count}
                          </span>
                        </div>
                      );
                    });
                  })()}
                </div>
              )}

              {selectedGapFilter && (
                <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg text-sm text-blue-800">
                  Showing only questions in "{getGapZoneInfo(selectedGapFilter).label}" range. Click chart or legend to clear filter.
                </div>
              )}
            </div>

            {/* Readiness vs Attractiveness Chart */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Readiness & Attractiveness
              </h3>
              <div className="relative w-full h-64">
                <svg viewBox="0 0 100 100" className="w-full h-full">
                  {/* Grid */}
                  {[20, 40, 60, 80].map(val => (
                    <React.Fragment key={val}>
                      <line x1="0" y1={100-val} x2="100" y2={100-val} stroke="#e5e7eb" strokeWidth="0.5" />
                      <line x1={val} y1="0" x2={val} y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                    </React.Fragment>
                  ))}

                  {/* Axes */}
                  <line x1="0" y1="100" x2="100" y2="100" stroke="#374151" strokeWidth="1" />
                  <line x1="0" y1="0" x2="0" y2="100" stroke="#374151" strokeWidth="1" />

                  {/* Current position */}
                  {assessment.overall_score > 0 && (
                    <circle
                      cx={assessment.readiness_score || 20}
                      cy={100 - (assessment.attractiveness_score || 20)}
                      r="3"
                      fill="#1e40af"
                      stroke="white"
                      strokeWidth="1"
                    />
                  )}
                </svg>

                <div className="absolute bottom-0 left-0 right-0 text-center text-xs text-gray-600">
                  Readiness
                </div>
                <div className="absolute left-0 top-0 bottom-0 flex items-center">
                  <div className="transform -rotate-90 text-xs text-gray-600 whitespace-nowrap">
                    Attractiveness
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Assessment Modal */}
      {showAssessmentModal && (
        <AssessmentModal
          assessmentId={assessment.assessment_id}
          allQuestions={allQuestions}
          answeredQuestions={answeredQuestions}
          onClose={() => {
            setShowAssessmentModal(false);
            loadAssessment(); // Refresh scores when closing
          }}
          onSave={handleModalSave}
        />
      )}

      {/* Question Editor Modal */}
      {selectedQuestion && (
        <QuestionModal
          question={selectedQuestion}
          assessmentId={assessment.assessment_id}
          onClose={() => setSelectedQuestion(null)}
          onSave={handleQuestionSave}
        />
      )}
    </div>
  );
};

export default GapAnalysis;
