import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronRight, FileText, Search } from 'lucide-react';
import { getAssessment, startAssessment } from '../services/api';
import QuestionModal from '../components/QuestionModal';
import './GapAnalysis.css';

const GapAnalysis = () => {
  const [assessment, setAssessment] = useState(null);
  const [loading, setLoading] = useState(true);
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    loadAssessment();
  }, []);

  const loadAssessment = async () => {
    setLoading(true);
    try {
      const result = await getAssessment();

      if (result.success) {
        setAssessment(result.data);
      } else {
        // No assessment exists, start one
        const startResult = await startAssessment();
        if (startResult.success) {
          const newResult = await getAssessment();
          setAssessment(newResult.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
    setLoading(false);
  };

  const handleSaveResponse = async (responseData) => {
    // The QuestionModal will handle the API call
    // After save, reload assessment to update scores
    await loadAssessment();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-gray-600">Loading assessment...</div>
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Assessment Found</h2>
          <button
            onClick={async () => {
              await startAssessment();
              loadAssessment();
            }}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Start Assessment
          </button>
        </div>
      </div>
    );
  }

  const categories = assessment.categories || {};
  const categoryKeys = Object.keys(categories);

  // Calculate progress percentage
  const progressPercent = assessment.total_questions > 0
    ? (assessment.answered_questions / assessment.total_questions) * 100
    : 0;

  // Prepare data for donut chart (simplified - you can enhance with a charting library)
  const donutData = categoryKeys.map(key => ({
    name: categories[key].name,
    value: categories[key].score || 0
  }));

  // Colors for donut chart segments
  const colors = [
    '#dc2626', // red
    '#ea580c', // orange
    '#facc15', // yellow
    '#84cc16', // lime
    '#22c55e', // green
    '#06b6d4', // cyan
    '#3b82f6', // blue
    '#8b5cf6'  // purple
  ];

  return (
    <div className="max-w-7xl mx-auto p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Gap Analysis</h1>
        <p className="text-gray-600">
          Complete the assessment to identify areas for improvement before exit
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Main Dashboard */}
        <div className="lg:col-span-2 space-y-6">
          {/* Overall Score Card */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Overall Score</h2>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search for question"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            <div className="grid grid-cols-3 gap-8 mb-6">
              <div>
                <p className="text-sm text-gray-500 mb-1">Initial score</p>
                <p className="text-3xl font-bold text-gray-400">
                  {Math.round(assessment.initial_score)}%
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500 mb-1">Current score</p>
                <p className="text-3xl font-bold text-gray-900">
                  {Math.round(assessment.overall_score)}%
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500 mb-1">Overall progress</p>
                <div className="relative pt-1">
                  <div className="overflow-hidden h-3 text-xs flex rounded-full bg-gray-200">
                    <div
                      style={{ width: `${Math.round(assessment.overall_score)}%` }}
                      className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-blue-600"
                    ></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Category Progress Bars */}
            <div className="space-y-4">
              {categoryKeys.map((catKey) => {
                const category = categories[catKey];
                const isExpanded = expandedCategory === catKey;
                const subjects = Object.keys(category.subjects || {});

                return (
                  <div key={catKey} className="border-b border-gray-100 last:border-b-0 pb-4 last:pb-0">
                    <div
                      className="flex items-center justify-between cursor-pointer hover:bg-gray-50 p-2 rounded"
                      onClick={() => setExpandedCategory(isExpanded ? null : catKey)}
                    >
                      <div className="flex items-center space-x-3 flex-1">
                        {isExpanded ? (
                          <ChevronDown size={20} className="text-gray-400" />
                        ) : (
                          <ChevronRight size={20} className="text-gray-400" />
                        )}
                        <span className="font-medium text-gray-900">{category.name}</span>
                      </div>
                      <div className="flex-1 mx-4">
                        <div className="relative pt-1">
                          <div className="overflow-hidden h-2 text-xs flex rounded-full bg-gray-200">
                            <div
                              style={{ width: `${Math.round(category.score)}%` }}
                              className={`shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center ${
                                category.score < 30
                                  ? 'bg-red-500'
                                  : category.score < 60
                                  ? 'bg-yellow-500'
                                  : 'bg-green-500'
                              }`}
                            ></div>
                          </div>
                        </div>
                      </div>
                    </div>

                    {/* Expanded Subject View (matching Gap_2) */}
                    {isExpanded && subjects.length > 0 && (
                      <div className="ml-8 mt-4 space-y-3 bg-gray-50 p-4 rounded">
                        <div className="flex items-center mb-4">
                          <input
                            type="checkbox"
                            id={`show-full-${catKey}`}
                            className="mr-2"
                          />
                          <label htmlFor={`show-full-${catKey}`} className="text-sm text-gray-600">
                            Show full question text
                          </label>
                        </div>

                        <div className="space-y-2">
                          <div className="grid grid-cols-2 gap-4 text-sm font-semibold text-gray-700 border-b pb-2">
                            <div>Subject</div>
                            <div>Score</div>
                          </div>

                          {subjects.map((subjectKey) => {
                            const subject = category.subjects[subjectKey];
                            const questions = subject.questions || [];

                            return (
                              <div key={subjectKey}>
                                <div className="grid grid-cols-2 gap-4 py-2">
                                  <div className="text-sm text-gray-900">{subject.name}</div>
                                  <div className="flex items-center">
                                    <div className="flex-1">
                                      <div className="overflow-hidden h-2 text-xs flex rounded-full bg-gray-200">
                                        <div
                                          style={{ width: `${Math.round(subject.score)}%` }}
                                          className={`shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center ${
                                            subject.score < 30
                                              ? 'bg-red-500'
                                              : subject.score < 60
                                              ? 'bg-yellow-500'
                                              : 'bg-green-500'
                                          }`}
                                        ></div>
                                      </div>
                                    </div>
                                  </div>
                                </div>

                                {/* Questions under this subject */}
                                {questions.map((q) => (
                                  <div
                                    key={q.id}
                                    className="ml-4 pl-4 border-l-2 border-gray-200 py-2 cursor-pointer hover:bg-white rounded"
                                    onClick={() => setSelectedQuestion({ ...q, category: catKey, subject: subject.name })}
                                  >
                                    <div className="text-sm text-gray-700">{q.question_text}</div>
                                    {q.answer_text && (
                                      <div className="text-xs text-gray-500 mt-1">
                                        Answer: {q.answer_text}
                                      </div>
                                    )}
                                  </div>
                                ))}
                              </div>
                            );
                          })}
                        </div>
                      </div>
                    )}
                  </div>
                );
              })}
            </div>
          </div>

          {/* Generate PDF Button */}
          <button className="flex items-center space-x-2 px-6 py-3 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition">
            <FileText size={20} />
            <span>Generate PDF Report</span>
          </button>
        </div>

        {/* Right Column - Charts */}
        <div className="space-y-6">
          {/* Donut Chart */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Subjects overall</h3>
            <div className="flex items-center justify-center">
              {/* Simplified Donut Chart - Replace with recharts or chart.js */}
              <div className="relative w-64 h-64">
                <svg viewBox="0 0 200 200" className="transform -rotate-90">
                  {donutData.map((item, index) => {
                    const total = donutData.reduce((sum, d) => sum + d.value, 0);
                    const percentage = total > 0 ? item.value / total : 0;
                    const startAngle = donutData
                      .slice(0, index)
                      .reduce((sum, d) => sum + (d.value / total), 0) * 360;
                    const endAngle = startAngle + percentage * 360;

                    // Simple circle segment (you'd want a proper charting library for production)
                    const largeArc = endAngle - startAngle > 180 ? 1 : 0;
                    const startX = 100 + 80 * Math.cos((startAngle * Math.PI) / 180);
                    const startY = 100 + 80 * Math.sin((startAngle * Math.PI) / 180);
                    const endX = 100 + 80 * Math.cos((endAngle * Math.PI) / 180);
                    const endY = 100 + 80 * Math.sin((endAngle * Math.PI) / 180);

                    return (
                      <path
                        key={index}
                        d={`M 100 100 L ${startX} ${startY} A 80 80 0 ${largeArc} 1 ${endX} ${endY} Z`}
                        fill={colors[index % colors.length]}
                        opacity="0.8"
                      />
                    );
                  })}
                  <circle cx="100" cy="100" r="50" fill="white" />
                </svg>
                <div className="absolute inset-0 flex items-center justify-center">
                  <div className="text-center">
                    <div className="text-4xl font-bold text-blue-900">
                      {Math.round(assessment.overall_score)}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Readiness vs Attractiveness Chart (matching Gap_1) */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">
              Business attractiveness & readiness
            </h3>
            <div className="relative w-full h-64">
              <svg viewBox="0 0 100 100" className="w-full h-full">
                {/* Grid lines */}
                <line x1="0" y1="80" x2="100" y2="80" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="0" y1="60" x2="100" y2="60" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="0" y1="40" x2="100" y2="40" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="0" y1="20" x2="100" y2="20" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="20" y1="0" x2="20" y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="40" y1="0" x2="40" y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="60" y1="0" x2="60" y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                <line x1="80" y1="0" x2="80" y2="100" stroke="#e5e7eb" strokeWidth="0.5" />

                {/* Axes */}
                <line x1="0" y1="100" x2="100" y2="100" stroke="#374151" strokeWidth="1" />
                <line x1="0" y1="0" x2="0" y2="100" stroke="#374151" strokeWidth="1" />

                {/* Trend lines (dashed) */}
                <line x1="0" y1="80" x2="100" y2="20" stroke="#facc15" strokeWidth="1" strokeDasharray="2,2" />
                <line x1="0" y1="60" x2="100" y2="40" stroke="#a3e635" strokeWidth="1" strokeDasharray="2,2" />
                <line x1="0" y1="40" x2="100" y2="60" stroke="#4ade80" strokeWidth="1" strokeDasharray="2,2" />

                {/* Current position */}
                <circle
                  cx={assessment.readiness_score || 20}
                  cy={100 - (assessment.attractiveness_score || 20)}
                  r="3"
                  fill="#1e40af"
                  stroke="white"
                  strokeWidth="1"
                />
              </svg>

              {/* Axis labels */}
              <div className="absolute bottom-0 left-0 right-0 text-center text-xs text-gray-600">
                Readiness
              </div>
              <div className="absolute left-0 top-0 bottom-0 flex items-center">
                <div className="transform -rotate-90 text-xs text-gray-600 whitespace-nowrap">
                  Attractiveness
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Question Modal (matching Gap_3) */}
      {selectedQuestion && (
        <QuestionModal
          question={selectedQuestion}
          assessmentId={assessment.assessment_id}
          onClose={() => setSelectedQuestion(null)}
          onSave={handleSaveResponse}
        />
      )}
    </div>
  );
};

export default GapAnalysis;
