import React, { useState, useEffect } from 'react';
import { FileText, PlayCircle, ChevronDown, ChevronRight, Edit2, Check } from 'lucide-react';
import { getAssessment, startAssessment } from '../services/api';
import AssessmentModal from '../components/AssessmentModal';
import QuestionModal from '../components/QuestionModal';
import './GapAnalysis.css';

const GapAnalysis = () => {
  const [assessment, setAssessment] = useState(null);
  const [allQuestions, setAllQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAssessmentModal, setShowAssessmentModal] = useState(false);
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [hoveredGapZone, setHoveredGapZone] = useState(null);
  const [selectedGapFilter, setSelectedGapFilter] = useState(null);

  useEffect(() => {
    loadAssessment();
    loadAllQuestions();
  }, []);

  const loadAssessment = async () => {
    setLoading(true);
    try {
      const result = await getAssessment();

      if (result.success) {
        setAssessment(result.data);
      } else {
        // No assessment exists, start one
        const startResult = await startAssessment();
        if (startResult.success) {
          const newResult = await getAssessment();
          setAssessment(newResult.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
    setLoading(false);
  };

  const loadAllQuestions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/assessment/questions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setAllQuestions(data.questions || []);
      }
    } catch (error) {
      console.error('Error loading questions:', error);
    }
  };

  const handleModalSave = async () => {
    // Reload assessment to update scores
    await loadAssessment();
  };

  // Get all answered questions from assessment
  const getAnsweredQuestions = () => {
    if (!assessment || !assessment.categories) return [];

    const answered = [];
    Object.keys(assessment.categories).forEach(catKey => {
      const category = assessment.categories[catKey];
      Object.keys(category.subjects || {}).forEach(subjectKey => {
        const subject = category.subjects[subjectKey];
        if (subject.questions) {
          answered.push(...subject.questions);
        }
      });
    });

    return answered;
  };

  // Get questions for a specific category
  const getCategoryQuestions = (categoryKey) => {
    return allQuestions.filter(q => q.category === categoryKey);
  };

  // Categorize questions by gap zone
  const getGapZone = (score) => {
    if (score >= 72) return 'no-gaps';
    if (score >= 67) return 'minor-gaps';
    if (score >= 50) return 'considerable-gaps';
    if (score >= 30) return 'critical-gaps';
    return 'very-critical-gaps';
  };

  const getGapZoneInfo = (zone) => {
    const zones = {
      'very-critical-gaps': { label: 'Very Critical Gaps', color: '#dc2626', range: '0-30%' },
      'critical-gaps': { label: 'Critical Gaps', color: '#f97316', range: '30-50%' },
      'considerable-gaps': { label: 'Considerable Gaps', color: '#eab308', range: '50-67%' },
      'minor-gaps': { label: 'Minor Gaps', color: '#84cc16', range: '67-72%' },
      'no-gaps': { label: 'No Gaps', color: '#16a34a', range: '72%+' }
    };
    return zones[zone];
  };

  // Get questions grouped by gap zones
  const getQuestionsByGapZone = () => {
    const answered = getAnsweredQuestions();
    const zones = {
      'very-critical-gaps': [],
      'critical-gaps': [],
      'considerable-gaps': [],
      'minor-gaps': [],
      'no-gaps': []
    };

    answered.forEach(answer => {
      const scorePercent = getScorePercent(answer.answer_value);
      const zone = getGapZone(scorePercent);
      zones[zone].push(answer);
    });

    return zones;
  };

  // Get answer for a question
  const getAnswer = (questionId) => {
    const answered = getAnsweredQuestions();
    return answered.find(a => a.question_id === questionId);
  };

  // Calculate score percentage from answer value
  const getScorePercent = (answerValue) => {
    if (answerValue === null || answerValue === undefined) return 0;
    return (answerValue / 5) * 100;
  };

  const getScoreColor = (percent) => {
    if (percent >= 72) return 'bg-green-600';  // Areas without gaps
    if (percent >= 67) return 'bg-lime-500';   // Areas with minor gaps
    if (percent >= 50) return 'bg-yellow-500'; // Areas with considerable gaps
    if (percent >= 30) return 'bg-orange-500'; // Areas with critical gaps
    return 'bg-red-600';                       // Areas with very critical gaps
  };

  const handleQuestionEdit = (question, categoryKey) => {
    const answer = getAnswer(question.question_id);
    setSelectedQuestion({
      ...question,
      category: categoryKey,
      subject: question.subject,
      answer_value: answer?.answer_value,
      answer_text: answer?.answer_text,
      comments: answer?.comments,
      id: answer?.id
    });
  };

  const handleQuestionSave = async () => {
    setSelectedQuestion(null);
    await loadAssessment();
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-gray-600">Loading assessment...</div>
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Assessment Found</h2>
          <button
            onClick={async () => {
              await startAssessment();
              loadAssessment();
            }}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Start Assessment
          </button>
        </div>
      </div>
    );
  }

  const categories = assessment.categories || {};
  const answeredQuestions = getAnsweredQuestions();
  const progressPercent = allQuestions.length > 0
    ? (assessment.answered_questions / allQuestions.length) * 100
    : 0;

  // Prepare data for donut chart
  const donutData = Object.keys(categories).map(key => ({
    name: categories[key].name,
    value: categories[key].score || 0
  })).filter(item => item.value > 0);

  const donutTotal = donutData.reduce((sum, item) => sum + item.value, 0);

  const colors = [
    '#dc2626', '#ea580c', '#facc15', '#84cc16',
    '#22c55e', '#06b6d4', '#3b82f6', '#8b5cf6'
  ];

  return (
    <div className="max-w-7xl mx-auto p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Gap Analysis</h1>
        <p className="text-gray-600">
          Complete the assessment to identify areas for improvement before exit
        </p>
      </div>

      {/* Overall Score Bar */}
      <div className="bg-white rounded-lg border-2 border-gray-200 p-6 mb-8">
        <div className="flex items-center justify-between mb-3">
          <div className="flex items-center space-x-2">
            <h2 className="text-lg font-bold text-gray-900">Overall Score</h2>
            {/* Info Tooltip */}
            <div className="relative group">
              <button className="w-5 h-5 rounded-full bg-gray-300 text-white text-xs font-bold flex items-center justify-center hover:bg-gray-400 transition cursor-help">
                ?
              </button>
              <div className="absolute left-0 top-full mt-2 w-80 bg-gray-900 text-white text-xs rounded-lg p-4 opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all z-10 shadow-xl">
                <div className="space-y-2">
                  <div className="font-bold text-sm mb-2">Exit Readiness Scoring Guide</div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-red-600 rounded"></div>
                    <span><strong>0-30%:</strong> Areas with very critical gaps</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-orange-500 rounded"></div>
                    <span><strong>30-50%:</strong> Areas with critical gaps</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-yellow-500 rounded"></div>
                    <span><strong>50-67%:</strong> Areas with considerable gaps</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-lime-500 rounded"></div>
                    <span><strong>67-72%:</strong> Areas with minor gaps (Target Zone ✓)</span>
                  </div>
                  <div className="flex items-center space-x-2">
                    <div className="w-3 h-3 bg-green-600 rounded"></div>
                    <span><strong>72%+:</strong> Areas without gaps (Best In Class 🏆)</span>
                  </div>
                  <div className="mt-3 pt-2 border-t border-gray-700 text-gray-300">
                    Businesses scoring 67%+ are in the optimal range for exit. Scores of 72%+ represent best-in-class preparation with minimal gaps.
                  </div>
                </div>
              </div>
            </div>
          </div>
          <span className="text-sm font-semibold text-gray-900">
            {Math.round(assessment.overall_score)}%
          </span>
        </div>

        {/* Progress Bar with Target Markers */}
        <div className="relative">
          <div className="w-full bg-gray-200 rounded-full h-4 relative">
            <div
              className={`h-4 rounded-full transition-all ${
                assessment.overall_score >= 72
                  ? 'bg-green-600'
                  : assessment.overall_score >= 67
                  ? 'bg-lime-500'
                  : assessment.overall_score >= 50
                  ? 'bg-yellow-500'
                  : assessment.overall_score >= 30
                  ? 'bg-orange-500'
                  : 'bg-red-600'
              }`}
              style={{ width: `${Math.round(assessment.overall_score)}%` }}
            ></div>
          </div>

          {/* Target Zone Markers */}
          <div className="relative h-8 mt-1">
            {/* 67% Marker - Targeted Green Zone */}
            <div className="absolute" style={{ left: '67%', transform: 'translateX(-50%)' }}>
              <div className="flex flex-col items-center">
                <div className="w-0.5 h-4 bg-lime-600"></div>
                <div className="text-xs font-semibold text-lime-700 whitespace-nowrap mt-1">
                  67%
                </div>
                <div className="text-xs text-gray-500 whitespace-nowrap">
                  Target
                </div>
              </div>
            </div>

            {/* 72% Marker - Best In Class */}
            <div className="absolute" style={{ left: '72%', transform: 'translateX(-50%)' }}>
              <div className="flex flex-col items-center">
                <div className="w-0.5 h-4 bg-green-600"></div>
                <div className="text-xs font-semibold text-green-700 whitespace-nowrap mt-1">
                  72%
                </div>
                <div className="text-xs text-gray-500 whitespace-nowrap">
                  Best in Class
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Status Text */}
        <div className="flex items-center justify-between mt-3">
          <span className="text-sm font-medium text-gray-700">
            {assessment.overall_score >= 72
              ? '🏆 Areas without gaps - Best In Class exit readiness!'
              : assessment.overall_score >= 67
              ? '✓ Areas with minor gaps - Target Zone reached'
              : assessment.overall_score >= 50
              ? 'Areas with considerable gaps - approaching target'
              : assessment.overall_score >= 30
              ? 'Areas with critical gaps - improvement needed'
              : 'Areas with very critical gaps - significant work needed'}
          </span>
          <span className="text-xs text-gray-500">
            {assessment.answered_questions} / {allQuestions.length} questions answered
          </span>
        </div>
      </div>

      {/* Start/Continue Assessment Card - Only show if not complete */}
      {progressPercent < 100 && (
        <div className="bg-gradient-to-r from-blue-50 to-blue-100 border-2 border-blue-200 rounded-lg p-8 mb-8">
          <div className="flex items-center justify-between">
            <div className="flex-1">
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                {assessment.answered_questions === 0 ? 'Ready to Begin?' : 'Continue Your Assessment'}
              </h2>
              <p className="text-gray-700 mb-4">
                {assessment.answered_questions === 0 
                  ? 'Answer 21 questions across 8 categories to assess your exit readiness'
                  : `You've answered ${assessment.answered_questions} of ${allQuestions.length} questions. Keep going!`
                }
              </p>
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => setShowAssessmentModal(true)}
                  className="px-8 py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-lg font-semibold flex items-center space-x-2"
                >
                  <PlayCircle size={24} />
                  <span>{assessment.answered_questions === 0 ? 'Start Assessment' : 'Continue Assessment'}</span>
                </button>
                {assessment.answered_questions > 0 && (
                  <div className="flex-1 max-w-xs">
                    <div className="flex justify-between text-sm text-gray-700 mb-1">
                      <span>Progress</span>
                      <span>{Math.round(progressPercent)}%</span>
                    </div>
                    <div className="w-full bg-blue-200 rounded-full h-3">
                      <div
                        className="bg-blue-600 h-3 rounded-full transition-all"
                        style={{ width: `${progressPercent}%` }}
                      ></div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Results Grid - Only show if some questions answered */}
      {assessment.answered_questions > 0 && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Scores */}
          <div className="lg:col-span-2 space-y-6">
            {/* Overall Score Card */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-6">Your Scores</h2>

              <div className="grid grid-cols-3 gap-8 mb-6">
                <div>
                  <p className="text-sm text-gray-500 mb-1">Initial score</p>
                  <p className="text-3xl font-bold text-gray-400">
                    {Math.round(assessment.initial_score)}%
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-500 mb-1">Current score</p>
                  <p className="text-3xl font-bold text-gray-900">
                    {Math.round(assessment.overall_score)}%
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-500 mb-1">Questions</p>
                  <p className="text-3xl font-bold text-blue-600">
                    {assessment.answered_questions}/{allQuestions.length}
                  </p>
                </div>
              </div>

              {/* Category Scores */}
              <div className="space-y-3">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="font-semibold text-gray-800">Category Breakdown</h3>
                  <span className="text-xs text-gray-500">Click to expand</span>
                </div>
                {Object.keys(categories)
                  .filter(catKey => {
                    // If gap filter is active, only show categories with questions in that zone
                    if (!selectedGapFilter) return true;

                    const categoryQuestions = getCategoryQuestions(catKey);
                    return categoryQuestions.some(q => {
                      const answer = getAnswer(q.question_id);
                      if (!answer) return false;
                      const scorePercent = getScorePercent(answer.answer_value);
                      return getGapZone(scorePercent) === selectedGapFilter;
                    });
                  })
                  .map((catKey) => {
                  const category = categories[catKey];
                  const score = category.score || 0;
                  const isExpanded = expandedCategory === catKey;
                  const categoryQuestions = getCategoryQuestions(catKey);

                  // Organize questions by subject
                  const questionsBySubject = {};
                  categoryQuestions.forEach(q => {
                    if (!questionsBySubject[q.subject]) {
                      questionsBySubject[q.subject] = [];
                    }
                    questionsBySubject[q.subject].push(q);
                  });

                  return (
                    <div key={catKey} className="border border-gray-200 rounded-lg overflow-hidden">
                      {/* Category Header */}
                      <div 
                        className="flex items-center space-x-3 cursor-pointer hover:bg-gray-50 p-3 transition"
                        onClick={() => setExpandedCategory(isExpanded ? null : catKey)}
                      >
                        {isExpanded ? (
                          <ChevronDown size={20} className="text-gray-600 flex-shrink-0" />
                        ) : (
                          <ChevronRight size={20} className="text-gray-600 flex-shrink-0" />
                        )}
                        <div className="flex-1">
                          <div className="flex items-center justify-between mb-1">
                            <span className="text-sm font-medium text-gray-700">
                              {category.name}
                            </span>
                            <span className="text-sm font-semibold text-gray-900">{Math.round(score)}%</span>
                          </div>
                          <div className="w-full bg-gray-200 rounded-full h-2">
                            <div
                              className={`h-2 rounded-full transition-all ${
                                score < 30
                                  ? 'bg-red-500'
                                  : score < 60
                                  ? 'bg-yellow-500'
                                  : 'bg-green-500'
                              }`}
                              style={{ width: `${score}%` }}
                            ></div>
                          </div>
                        </div>
                      </div>

                      {/* Expanded Questions */}
                      {isExpanded && (
                        <div className="border-t border-gray-200 bg-gray-50 p-4">
                          <div className="space-y-4">
                            {Object.keys(questionsBySubject).map((subjectName) => {
                              const questions = questionsBySubject[subjectName];

                              return (
                                <div key={subjectName} className="space-y-2">
                                  <h4 className="font-semibold text-sm text-gray-800 mb-2">
                                    {subjectName}
                                  </h4>

                                  {questions.map((question) => {
                                    const answer = getAnswer(question.question_id);
                                    const isAnswered = answer !== undefined;
                                    const scorePercent = getScorePercent(answer?.answer_value);

                                    // If gap filter is active, only show questions in that zone
                                    if (selectedGapFilter && isAnswered) {
                                      const questionZone = getGapZone(scorePercent);
                                      if (questionZone !== selectedGapFilter) return null;
                                    }

                                    return (
                                      <div
                                        key={question.question_id}
                                        className={`p-3 rounded-lg border transition ${
                                          isAnswered
                                            ? 'border-green-200 bg-white'
                                            : 'border-gray-200 bg-white'
                                        }`}
                                      >
                                        <div className="flex items-start justify-between mb-2">
                                          <p className="text-sm text-gray-700 flex-1 pr-3">
                                            {question.question_text}
                                          </p>
                                          <button
                                            onClick={() => handleQuestionEdit(question, catKey)}
                                            className="flex-shrink-0 p-1 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded transition"
                                            title="Edit answer"
                                          >
                                            <Edit2 size={16} />
                                          </button>
                                        </div>

                                        {/* Progress Bar */}
                                        {isAnswered ? (
                                          <>
                                            <div className="flex items-center justify-between mb-1">
                                              <span className="text-xs text-gray-600">Score</span>
                                              <span className="text-xs font-semibold text-gray-900">
                                                {Math.round(scorePercent)}%
                                              </span>
                                            </div>
                                            <div className="w-full bg-gray-200 rounded-full h-1.5 mb-2">
                                              <div
                                                className={`h-1.5 rounded-full transition-all ${getScoreColor(scorePercent)}`}
                                                style={{ width: `${scorePercent}%` }}
                                              ></div>
                                            </div>
                                            <div className="flex items-center space-x-2">
                                              <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-medium ${
                                                scorePercent >= 72
                                                  ? 'bg-green-100 text-green-800'
                                                  : scorePercent >= 67
                                                  ? 'bg-lime-100 text-lime-800'
                                                  : scorePercent >= 50
                                                  ? 'bg-yellow-100 text-yellow-800'
                                                  : scorePercent >= 30
                                                  ? 'bg-orange-100 text-orange-800'
                                                  : 'bg-red-100 text-red-800'
                                              }`}>
                                                <Check size={12} className="mr-1" />
                                                {answer.answer_text}
                                              </span>
                                            </div>
                                          </>
                                        ) : (
                                          <div className="flex items-center">
                                            <div className="flex-1 bg-gray-200 rounded-full h-1.5">
                                              <div className="h-1.5 rounded-full bg-gray-300" style={{ width: '0%' }}></div>
                                            </div>
                                            <span className="text-xs text-gray-500 ml-2 whitespace-nowrap">
                                              Not answered
                                            </span>
                                          </div>
                                        )}
                                      </div>
                                    );
                                  })}
                                </div>
                              );
                            })}
                          </div>
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Generate PDF Button */}
            <button className="flex items-center space-x-2 px-6 py-3 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition">
              <FileText size={20} />
              <span>Generate PDF Report</span>
            </button>

            {/* Retake Assessment Button */}
            {progressPercent === 100 && (
              <button 
                onClick={() => setShowAssessmentModal(true)}
                className="flex items-center space-x-2 px-6 py-3 bg-blue-600 text-white border border-blue-600 rounded-lg hover:bg-blue-700 transition"
              >
                <PlayCircle size={20} />
                <span>Retake Assessment</span>
              </button>
            )}
          </div>

          {/* Right Column - Charts */}
          <div className="space-y-6">
            {/* Interactive Donut Chart by Gap Zones */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Questions by Gap Level
                {selectedGapFilter && (
                  <button
                    onClick={() => setSelectedGapFilter(null)}
                    className="ml-3 text-xs text-blue-600 hover:text-blue-700 font-normal"
                  >
                    (Clear filter)
                  </button>
                )}
              </h3>
              <div className="flex items-center justify-center">
                {assessment.answered_questions > 0 ? (
                  <div className="relative w-64 h-64">
                    {/* SVG Donut Chart */}
                    <svg viewBox="0 0 200 200" className="transform -rotate-90">
                      {(() => {
                        const questionsByZone = getQuestionsByGapZone();
                        const totalAnswered = assessment.answered_questions;
                        const zones = ['very-critical-gaps', 'critical-gaps', 'considerable-gaps', 'minor-gaps', 'no-gaps'];
                        let currentAngle = 0;

                        return zones.map((zone) => {
                          const count = questionsByZone[zone].length;
                          if (count === 0) return null;

                          const percentage = count / totalAnswered;
                          const sweepAngle = percentage * 360;
                          const zoneInfo = getGapZoneInfo(zone);

                          // Calculate path
                          const startAngle = currentAngle;
                          const endAngle = currentAngle + sweepAngle;
                          const isHovered = hoveredGapZone === zone;
                          const isSelected = selectedGapFilter === zone;
                          const radius = isHovered || isSelected ? 85 : 80;

                          const largeArc = sweepAngle > 180 ? 1 : 0;
                          const startX = 100 + radius * Math.cos((startAngle * Math.PI) / 180);
                          const startY = 100 + radius * Math.sin((startAngle * Math.PI) / 180);
                          const endX = 100 + radius * Math.cos((endAngle * Math.PI) / 180);
                          const endY = 100 + radius * Math.sin((endAngle * Math.PI) / 180);

                          currentAngle = endAngle;

                          return (
                            <g key={zone}>
                              <path
                                d={`M 100 100 L ${startX} ${startY} A ${radius} ${radius} 0 ${largeArc} 1 ${endX} ${endY} Z`}
                                fill={zoneInfo.color}
                                opacity={isHovered || isSelected ? 1 : 0.8}
                                onMouseEnter={() => setHoveredGapZone(zone)}
                                onMouseLeave={() => setHoveredGapZone(null)}
                                onClick={() => setSelectedGapFilter(selectedGapFilter === zone ? null : zone)}
                                className="cursor-pointer transition-all duration-200"
                                style={{ transformOrigin: '100px 100px' }}
                              />
                            </g>
                          );
                        });
                      })()}

                      {/* Center circle */}
                      <circle cx="100" cy="100" r="50" fill="white" />
                    </svg>

                    {/* Center text */}
                    <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                      <div className="text-center">
                        {hoveredGapZone ? (
                          <>
                            <div className="text-3xl font-bold" style={{ color: getGapZoneInfo(hoveredGapZone).color }}>
                              {getQuestionsByGapZone()[hoveredGapZone].length}
                            </div>
                            <div className="text-xs text-gray-600 mt-1">
                              {getGapZoneInfo(hoveredGapZone).label}
                            </div>
                          </>
                        ) : (
                          <>
                            <div className="text-4xl font-bold text-blue-900">
                              {Math.round(assessment.overall_score)}
                            </div>
                            <div className="text-xs text-gray-600">Score</div>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <div className="text-gray-400 mb-2">
                      <svg className="w-24 h-24 mx-auto" fill="currentColor" viewBox="0 0 20 20">
                        <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z" clipRule="evenodd" />
                      </svg>
                    </div>
                    <p className="text-gray-600">No data yet</p>
                    <p className="text-sm text-gray-500">Answer questions to see distribution</p>
                  </div>
                )}
              </div>

              {/* Legend */}
              {assessment.answered_questions > 0 && (
                <div className="mt-6 space-y-2">
                  {(() => {
                    const questionsByZone = getQuestionsByGapZone();
                    const zones = ['no-gaps', 'minor-gaps', 'considerable-gaps', 'critical-gaps', 'very-critical-gaps'];

                    return zones.map((zone) => {
                      const count = questionsByZone[zone].length;
                      if (count === 0) return null;

                      const zoneInfo = getGapZoneInfo(zone);
                      const isHovered = hoveredGapZone === zone;
                      const isSelected = selectedGapFilter === zone;

                      return (
                        <div
                          key={zone}
                          className={`flex items-center justify-between p-2 rounded cursor-pointer transition ${
                            isHovered || isSelected ? 'bg-gray-100' : 'hover:bg-gray-50'
                          }`}
                          onMouseEnter={() => setHoveredGapZone(zone)}
                          onMouseLeave={() => setHoveredGapZone(null)}
                          onClick={() => setSelectedGapFilter(selectedGapFilter === zone ? null : zone)}
                        >
                          <div className="flex items-center space-x-2">
                            <div
                              className="w-3 h-3 rounded"
                              style={{ backgroundColor: zoneInfo.color }}
                            ></div>
                            <span className="text-sm text-gray-700">
                              {zoneInfo.label} ({zoneInfo.range})
                            </span>
                          </div>
                          <span className={`text-sm font-semibold ${isSelected ? 'text-blue-600' : 'text-gray-900'}`}>
                            {count}
                          </span>
                        </div>
                      );
                    });
                  })()}
                </div>
              )}

              {selectedGapFilter && (
                <div className="mt-4 p-3 bg-blue-50 border border-blue-200 rounded-lg text-sm text-blue-800">
                  Showing only questions in "{getGapZoneInfo(selectedGapFilter).label}" range. Click chart or legend to clear filter.
                </div>
              )}
            </div>

            {/* Readiness vs Attractiveness Chart */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Readiness & Attractiveness
              </h3>
              <div className="relative w-full h-64">
                <svg viewBox="0 0 100 100" className="w-full h-full">
                  {/* Grid */}
                  {[20, 40, 60, 80].map(val => (
                    <React.Fragment key={val}>
                      <line x1="0" y1={100-val} x2="100" y2={100-val} stroke="#e5e7eb" strokeWidth="0.5" />
                      <line x1={val} y1="0" x2={val} y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                    </React.Fragment>
                  ))}

                  {/* Axes */}
                  <line x1="0" y1="100" x2="100" y2="100" stroke="#374151" strokeWidth="1" />
                  <line x1="0" y1="0" x2="0" y2="100" stroke="#374151" strokeWidth="1" />

                  {/* Current position */}
                  {assessment.overall_score > 0 && (
                    <circle
                      cx={assessment.readiness_score || 20}
                      cy={100 - (assessment.attractiveness_score || 20)}
                      r="3"
                      fill="#1e40af"
                      stroke="white"
                      strokeWidth="1"
                    />
                  )}
                </svg>

                <div className="absolute bottom-0 left-0 right-0 text-center text-xs text-gray-600">
                  Readiness
                </div>
                <div className="absolute left-0 top-0 bottom-0 flex items-center">
                  <div className="transform -rotate-90 text-xs text-gray-600 whitespace-nowrap">
                    Attractiveness
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Assessment Modal */}
      {showAssessmentModal && (
        <AssessmentModal
          assessmentId={assessment.id || assessment.assessment_id}
          allQuestions={allQuestions}
          answeredQuestions={answeredQuestions}
          onClose={() => {
            setShowAssessmentModal(false);
            loadAssessment(); // Refresh scores when closing
          }}
          onSave={handleModalSave}
        />
      )}

      {/* Question Editor Modal */}
      {selectedQuestion && (
        <QuestionModal
          question={selectedQuestion}
          assessmentId={assessment.id || assessment.assessment_id}
          onClose={() => setSelectedQuestion(null)}
          onSave={handleQuestionSave}
        />
      )}
    </div>
  );
};

export default GapAnalysis;
