import React, { useState, useEffect } from 'react';
import { X, Send } from 'lucide-react';
import { saveAssessmentResponse } from '../services/api';

const QuestionModal = ({ question, assessmentId, onClose, onSave }) => {
  const [selectedValue, setSelectedValue] = useState(null);
  const [comments, setComments] = useState('');
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (question) {
      setSelectedValue(question.answer_value);
      setComments(question.comments || '');
    }
  }, [question]);

  const handleSave = async () => {
    setSaving(true);
    try {
      const responseData = {
        assessment_id: assessmentId,
        question_id: question.question_id,
        category: question.category,
        subject: question.subject,
        question_text: question.question_text,
        answer_value: selectedValue,
        answer_text: getAnswerText(selectedValue),
        comments: comments,
        rule_of_thumb: question.rule_of_thumb,
        considerations: question.considerations,
        related_task: question.related_task
      };

      const result = await saveAssessmentResponse(responseData);

      if (result.success) {
        if (onSave) {
          await onSave(responseData);
        }
        onClose();
      } else {
        alert('Failed to save response');
      }
    } catch (error) {
      console.error('Error saving response:', error);
      alert('Error saving response');
    }
    setSaving(false);
  };

  const getAnswerText = (value) => {
    const labels = [
      'No advantage at all',
      'Very little',
      'Some advantage',
      'Moderate advantage',
      'Strong advantage',
      'Great advantage'
    ];
    return labels[value] || '';
  };

  if (!question) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-5xl max-h-[90vh] overflow-hidden flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-900">Update Score</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="space-y-6">
            {/* Question Section */}
            <div className="space-y-4">
              <div>
                <h3 className="text-sm font-semibold text-gray-700 mb-2">Subject</h3>
                <p className="text-gray-900">{question.subject}</p>
              </div>

              <div>
                <h3 className="text-sm font-semibold text-gray-700 mb-2">Question</h3>
                <p className="text-gray-900">{question.question_text}</p>
              </div>

              {/* Radio Button Scale */}
              <div>
                <h3 className="text-sm font-semibold text-gray-700 mb-3">Answer</h3>
                <div className="flex items-center justify-between space-x-4 py-4">
                  <span className="text-xs text-gray-600 whitespace-nowrap">No advantage at all</span>
                  <div className="flex space-x-6">
                    {[0, 1, 2, 3, 4, 5].map((value) => (
                      <label key={value} className="cursor-pointer relative group">
                        <input
                          type="radio"
                          name="answer"
                          value={value}
                          checked={selectedValue === value}
                          onChange={() => setSelectedValue(value)}
                          className="appearance-none w-7 h-7 border-2 border-gray-300 rounded-full cursor-pointer transition
                                   checked:border-red-600 checked:bg-red-600
                                   hover:border-red-400
                                   focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2"
                        />
                        {selectedValue === value && (
                          <span className="absolute inset-0 flex items-center justify-center">
                            <span className="w-3 h-3 bg-white rounded-full"></span>
                          </span>
                        )}
                        {/* Tooltip */}
                        <span className="absolute bottom-full left-1/2 transform -translate-x-1/2 mb-2 px-3 py-1 bg-gray-800 text-white text-xs rounded opacity-0 group-hover:opacity-100 transition whitespace-nowrap pointer-events-none z-10">
                          {getAnswerText(value)}
                        </span>
                      </label>
                    ))}
                  </div>
                  <span className="text-xs text-gray-600 whitespace-nowrap">Great advantage</span>
                </div>
              </div>

              {/* Save Button */}
              <button
                onClick={handleSave}
                disabled={selectedValue === null || saving}
                className="w-full sm:w-auto px-8 py-3 bg-blue-900 text-white rounded-lg hover:bg-blue-800 transition disabled:bg-gray-300 disabled:cursor-not-allowed flex items-center justify-center space-x-2 font-semibold"
              >
                <span>{saving ? 'Saving...' : 'Save'}</span>
                {!saving && <span>💾</span>}
              </button>
            </div>

            {/* Rule of Thumb */}
            {question.rule_of_thumb && (
              <div className="bg-blue-50 rounded-lg p-4 border border-blue-100">
                <h3 className="font-semibold text-gray-900 mb-2 flex items-center">
                  <span className="mr-2">💡</span>
                  Rule of thumb
                </h3>
                <p className="text-sm text-gray-700">{question.rule_of_thumb}</p>
              </div>
            )}

            {/* Considerations */}
            {question.considerations && (
              <div className="bg-gray-50 rounded-lg p-4 border border-gray-200">
                <h3 className="font-semibold text-gray-900 mb-2 flex items-center">
                  <span className="mr-2">📋</span>
                  Consider
                </h3>
                {Array.isArray(question.considerations) ? (
                  <ul className="list-disc list-inside space-y-2 text-sm text-gray-700">
                    {question.considerations.map((item, index) => (
                      <li key={index}>{item}</li>
                    ))}
                  </ul>
                ) : (
                  <p className="text-sm text-gray-700">{question.considerations}</p>
                )}
              </div>
            )}

            {/* Related Task */}
            {question.related_task && (
              <div className="bg-yellow-50 rounded-lg p-4 border border-yellow-100">
                <h3 className="font-semibold text-gray-900 mb-2 flex items-center">
                  <span className="mr-2">✓</span>
                  Related task
                </h3>
                <p className="text-sm text-gray-700">{question.related_task}</p>
                <p className="text-xs text-gray-500 mt-2">
                  This task will be created if your score is low
                </p>
              </div>
            )}

            {/* Comments */}
            <div>
              <h3 className="font-semibold text-gray-900 mb-2">Comments (Optional)</h3>
              <div className="relative">
                <textarea
                  value={comments}
                  onChange={(e) => setComments(e.target.value)}
                  placeholder="Add any notes or comments about this question..."
                  rows={3}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none text-sm"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuestionModal;
