import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronRight, FileText, Search, PlayCircle } from 'lucide-react';
import { getAssessment, startAssessment } from '../services/api';
import QuestionModal from '../components/QuestionModal';
import './GapAnalysis.css';

const GapAnalysis = () => {
  const [assessment, setAssessment] = useState(null);
  const [allQuestions, setAllQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedCategory, setExpandedCategory] = useState(null);
  const [selectedQuestion, setSelectedQuestion] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showAllQuestions, setShowAllQuestions] = useState(false);

  useEffect(() => {
    loadAssessment();
    loadAllQuestions();
  }, []);

  const loadAssessment = async () => {
    setLoading(true);
    try {
      const result = await getAssessment();

      if (result.success) {
        setAssessment(result.data);
      } else {
        // No assessment exists, start one
        const startResult = await startAssessment();
        if (startResult.success) {
          const newResult = await getAssessment();
          setAssessment(newResult.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
    setLoading(false);
  };

  const loadAllQuestions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/assessment/questions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setAllQuestions(data.questions || []);
      }
    } catch (error) {
      console.error('Error loading questions:', error);
    }
  };

  const handleSaveResponse = async (responseData) => {
    // Reload assessment to update scores
    await loadAssessment();
  };

  const handleStartAssessment = () => {
    setShowAllQuestions(true);
    // Auto-expand first category
    if (allQuestions.length > 0) {
      setExpandedCategory(allQuestions[0].category);
    }
  };

  // Organize questions by category and subject
  const organizeQuestions = () => {
    const organized = {};

    allQuestions.forEach(q => {
      if (!organized[q.category]) {
        organized[q.category] = {
          name: q.category_display,
          subjects: {}
        };
      }

      if (!organized[q.category].subjects[q.subject]) {
        organized[q.category].subjects[q.subject] = {
          name: q.subject,
          questions: []
        };
      }

      organized[q.category].subjects[q.subject].questions.push(q);
    });

    return organized;
  };

  const questionsByCategory = organizeQuestions();

  // Get answered questions from assessment
  const getAnsweredQuestion = (questionId) => {
    if (!assessment || !assessment.categories) return null;

    for (const catKey of Object.keys(assessment.categories)) {
      const category = assessment.categories[catKey];
      for (const subjectKey of Object.keys(category.subjects || {})) {
        const subject = category.subjects[subjectKey];
        const found = subject.questions?.find(q => q.question_id === questionId);
        if (found) return found;
      }
    }
    return null;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-gray-600">Loading assessment...</div>
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Assessment Found</h2>
          <button
            onClick={async () => {
              await startAssessment();
              loadAssessment();
            }}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Start Assessment
          </button>
        </div>
      </div>
    );
  }

  const categories = assessment.categories || {};
  const categoryKeys = Object.keys(categories);

  // Calculate progress percentage
  const progressPercent = assessment.total_questions > 0
    ? (assessment.answered_questions / assessment.total_questions) * 100
    : 0;

  // Prepare data for donut chart
  const donutData = categoryKeys.map(key => ({
    name: categories[key].name,
    value: categories[key].score || 0
  })).filter(item => item.value > 0);

  const donutTotal = donutData.reduce((sum, item) => sum + item.value, 0);

  // Colors for donut chart segments
  const colors = [
    '#dc2626', '#ea580c', '#facc15', '#84cc16',
    '#22c55e', '#06b6d4', '#3b82f6', '#8b5cf6'
  ];

  return (
    <div className="max-w-7xl mx-auto p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Gap Analysis</h1>
        <p className="text-gray-600">
          Complete the assessment to identify areas for improvement before exit
        </p>
      </div>

      {/* Start Assessment Button */}
      {assessment.answered_questions === 0 && !showAllQuestions && (
        <div className="bg-blue-50 border-2 border-blue-200 rounded-lg p-8 mb-8 text-center">
          <PlayCircle size={48} className="mx-auto text-blue-600 mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-2">Ready to Begin?</h2>
          <p className="text-gray-600 mb-6">
            Answer 21 questions across 8 categories to assess your exit readiness
          </p>
          <button
            onClick={handleStartAssessment}
            className="px-8 py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-lg font-semibold flex items-center space-x-2 mx-auto"
          >
            <PlayCircle size={24} />
            <span>Start Assessment</span>
          </button>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Main Dashboard */}
        <div className="lg:col-span-2 space-y-6">
          {/* Overall Score Card */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <div className="flex items-center justify-between mb-6">
              <h2 className="text-xl font-bold text-gray-900">Overall Score</h2>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="Search for question"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            <div className="grid grid-cols-3 gap-8 mb-6">
              <div>
                <p className="text-sm text-gray-500 mb-1">Initial score</p>
                <p className="text-3xl font-bold text-gray-400">
                  {Math.round(assessment.initial_score)}%
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500 mb-1">Current score</p>
                <p className="text-3xl font-bold text-gray-900">
                  {Math.round(assessment.overall_score)}%
                </p>
              </div>
              <div>
                <p className="text-sm text-gray-500 mb-1">Progress</p>
                <div className="relative pt-1">
                  <div className="flex mb-2 items-center justify-between">
                    <div>
                      <span className="text-xs font-semibold inline-block text-blue-600">
                        {assessment.answered_questions} / {allQuestions.length}
                      </span>
                    </div>
                  </div>
                  <div className="overflow-hidden h-3 text-xs flex rounded-full bg-gray-200">
                    <div
                      style={{ width: `${(assessment.answered_questions / allQuestions.length) * 100}%` }}
                      className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-blue-600"
                    ></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Category List with Questions */}
            <div className="space-y-4">
              {showAllQuestions || assessment.answered_questions > 0 ? (
                Object.keys(questionsByCategory).map((catKey) => {
                  const category = questionsByCategory[catKey];
                  const isExpanded = expandedCategory === catKey;
                  const subjects = Object.keys(category.subjects || {});

                  // Get score from assessment if available
                  const categoryScore = categories[catKey]?.score || 0;

                  return (
                    <div key={catKey} className="border-b border-gray-100 last:border-b-0 pb-4 last:pb-0">
                      <div
                        className="flex items-center justify-between cursor-pointer hover:bg-gray-50 p-2 rounded"
                        onClick={() => setExpandedCategory(isExpanded ? null : catKey)}
                      >
                        <div className="flex items-center space-x-3 flex-1">
                          {isExpanded ? (
                            <ChevronDown size={20} className="text-gray-400" />
                          ) : (
                            <ChevronRight size={20} className="text-gray-400" />
                          )}
                          <span className="font-medium text-gray-900">{category.name}</span>
                        </div>
                        <div className="flex-1 mx-4">
                          <div className="relative pt-1">
                            <div className="overflow-hidden h-2 text-xs flex rounded-full bg-gray-200">
                              <div
                                style={{ width: `${Math.round(categoryScore)}%` }}
                                className={`shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center ${
                                  categoryScore < 30
                                    ? 'bg-red-500'
                                    : categoryScore < 60
                                    ? 'bg-yellow-500'
                                    : 'bg-green-500'
                                }`}
                              ></div>
                            </div>
                          </div>
                        </div>
                        <span className="text-sm font-semibold text-gray-600">
                          {Math.round(categoryScore)}%
                        </span>
                      </div>

                      {/* Expanded Questions View */}
                      {isExpanded && (
                        <div className="ml-8 mt-4 space-y-3 bg-gray-50 p-4 rounded">
                          {subjects.map((subjectKey) => {
                            const subject = category.subjects[subjectKey];
                            const questions = subject.questions || [];

                            return (
                              <div key={subjectKey} className="space-y-2">
                                <h4 className="font-semibold text-gray-800 text-sm">{subject.name}</h4>

                                {questions.map((q) => {
                                  const answered = getAnsweredQuestion(q.question_id);

                                  return (
                                    <div
                                      key={q.question_id}
                                      className="ml-4 p-3 border-l-2 border-gray-300 cursor-pointer hover:bg-white hover:border-blue-500 rounded transition"
                                      onClick={() => setSelectedQuestion({
                                        ...q,
                                        category: catKey,
                                        subject: subject.name,
                                        answer_value: answered?.answer_value,
                                        answer_text: answered?.answer_text,
                                        comments: answered?.comments,
                                        id: answered?.id
                                      })}
                                    >
                                      <div className="flex items-start justify-between">
                                        <div className="flex-1">
                                          <div className="text-sm text-gray-700">{q.question_text}</div>
                                          {answered && (
                                            <div className="mt-2 flex items-center space-x-2">
                                              <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                                 Answered
                                              </span>
                                              <span className="text-xs text-gray-500">
                                                {answered.answer_text}
                                              </span>
                                            </div>
                                          )}
                                        </div>
                                        {!answered && (
                                          <span className="ml-2 text-xs text-gray-400 whitespace-nowrap">
                                            Click to answer
                                          </span>
                                        )}
                                      </div>
                                    </div>
                                  );
                                })}
                              </div>
                            );
                          })}
                        </div>
                      )}
                    </div>
                  );
                })
              ) : (
                <div className="text-center py-12 bg-gray-50 rounded-lg">
                  <PlayCircle size={48} className="mx-auto text-gray-400 mb-4" />
                  <p className="text-gray-600 mb-4">Click "Start Assessment" above to begin</p>
                </div>
              )}
            </div>
          </div>

          {/* Generate PDF Button */}
          <button className="flex items-center space-x-2 px-6 py-3 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition">
            <FileText size={20} />
            <span>Generate PDF Report</span>
          </button>
        </div>

        {/* Right Column - Charts */}
        <div className="space-y-6">
          {/* Donut Chart */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">Subjects overall</h3>
            <div className="flex items-center justify-center">
              {donutData.length > 0 && donutTotal > 0 ? (
                <div className="relative w-64 h-64">
                  <svg viewBox="0 0 200 200" className="transform -rotate-90">
                    {donutData.map((item, index) => {
                      const percentage = item.value / donutTotal;
                      const startAngle = donutData
                        .slice(0, index)
                        .reduce((sum, d) => sum + (d.value / donutTotal), 0) * 360;
                      const endAngle = startAngle + percentage * 360;

                      const largeArc = endAngle - startAngle > 180 ? 1 : 0;
                      const startX = 100 + 80 * Math.cos((startAngle * Math.PI) / 180);
                      const startY = 100 + 80 * Math.sin((startAngle * Math.PI) / 180);
                      const endX = 100 + 80 * Math.cos((endAngle * Math.PI) / 180);
                      const endY = 100 + 80 * Math.sin((endAngle * Math.PI) / 180);

                      return (
                        <path
                          key={index}
                          d={`M 100 100 L ${startX} ${startY} A 80 80 0 ${largeArc} 1 ${endX} ${endY} Z`}
                          fill={colors[index % colors.length]}
                          opacity="0.8"
                        />
                      );
                    })}
                    <circle cx="100" cy="100" r="50" fill="white" />
                  </svg>
                  <div className="absolute inset-0 flex items-center justify-center">
                    <div className="text-center">
                      <div className="text-4xl font-bold text-blue-900">
                        {Math.round(assessment.overall_score)}
                      </div>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-12">
                  <div className="text-gray-400 mb-2">
                    <svg className="w-24 h-24 mx-auto" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v2H7a1 1 0 100 2h2v2a1 1 0 102 0v-2h2a1 1 0 100-2h-2V7z" clipRule="evenodd" />
                    </svg>
                  </div>
                  <p className="text-gray-600">No data yet</p>
                  <p className="text-sm text-gray-500">Answer questions to see your score</p>
                </div>
              )}
            </div>
          </div>

          {/* Readiness vs Attractiveness Chart */}
          <div className="bg-white rounded-lg border border-gray-200 p-6">
            <h3 className="text-lg font-bold text-gray-900 mb-4">
              Business attractiveness & readiness
            </h3>
            <div className="relative w-full h-64">
              <svg viewBox="0 0 100 100" className="w-full h-full">
                {/* Grid */}
                {[20, 40, 60, 80].map(val => (
                  <React.Fragment key={val}>
                    <line x1="0" y1={100-val} x2="100" y2={100-val} stroke="#e5e7eb" strokeWidth="0.5" />
                    <line x1={val} y1="0" x2={val} y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                  </React.Fragment>
                ))}

                {/* Axes */}
                <line x1="0" y1="100" x2="100" y2="100" stroke="#374151" strokeWidth="1" />
                <line x1="0" y1="0" x2="0" y2="100" stroke="#374151" strokeWidth="1" />

                {/* Current position */}
                {assessment.overall_score > 0 && (
                  <circle
                    cx={assessment.readiness_score || 20}
                    cy={100 - (assessment.attractiveness_score || 20)}
                    r="3"
                    fill="#1e40af"
                    stroke="white"
                    strokeWidth="1"
                  />
                )}
              </svg>

              <div className="absolute bottom-0 left-0 right-0 text-center text-xs text-gray-600">
                Readiness
              </div>
              <div className="absolute left-0 top-0 bottom-0 flex items-center">
                <div className="transform -rotate-90 text-xs text-gray-600 whitespace-nowrap">
                  Attractiveness
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Question Modal */}
      {selectedQuestion && (
        <QuestionModal
          question={selectedQuestion}
          assessmentId={assessment.assessment_id}
          onClose={() => setSelectedQuestion(null)}
          onSave={handleSaveResponse}
        />
      )}
    </div>
  );
};

export default GapAnalysis;
