import React, { useState, useEffect } from 'react';
import { X, Send } from 'lucide-react';
import { saveAssessmentResponse } from '../services/api';

const QuestionModal = ({ question, assessmentId, onClose, onSave }) => {
  const [selectedValue, setSelectedValue] = useState(null);
  const [comments, setComments] = useState('');
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (question) {
      setSelectedValue(question.answer_value);
      setComments(question.comments || '');
    }
  }, [question]);

  const handleSave = async () => {
    setSaving(true);
    try {
      const responseData = {
        assessment_id: assessmentId,
        question_id: question.question_id,
        category: question.category,
        subject: question.subject,
        question_text: question.question_text,
        answer_value: selectedValue,
        answer_text: getAnswerText(selectedValue),
        comments: comments,
        rule_of_thumb: question.rule_of_thumb,
        considerations: question.considerations,
        related_task: question.related_task
      };

      const result = await saveAssessmentResponse(responseData);

      if (result.success) {
        if (onSave) {
          await onSave(responseData);
        }
        onClose();
      } else {
        alert('Failed to save response');
      }
    } catch (error) {
      console.error('Error saving response:', error);
      alert('Error saving response');
    }
    setSaving(false);
  };

  const getAnswerText = (value) => {
    const labels = [
      'No advantage at all',
      'Very little',
      'Some advantage',
      'Moderate advantage',
      'Strong advantage',
      'Great advantage'
    ];
    return labels[value] || '';
  };

  if (!question) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-900">Update score</h2>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600 transition"
          >
            <X size={24} />
          </button>
        </div>

        {/* Content */}
        <div className="flex-1 overflow-y-auto p-6">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
            {/* Left: Question and Answer */}
            <div className="lg:col-span-2 space-y-6">
              {/* Question Table */}
              <div className="border border-gray-200 rounded-lg overflow-hidden">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-4 py-3 text-left text-sm font-semibold text-gray-700">
                        Subject
                      </th>
                      <th className="px-4 py-3 text-left text-sm font-semibold text-gray-700">
                        Question
                      </th>
                      <th className="px-4 py-3 text-left text-sm font-semibold text-gray-700">
                        Answer
                      </th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td className="px-4 py-4 text-sm text-gray-900 align-top">
                        {question.subject}
                      </td>
                      <td className="px-4 py-4 text-sm text-gray-900 align-top">
                        {question.question_text}
                      </td>
                      <td className="px-4 py-4 align-top">
                        {/* Radio Button Scale */}
                        <div className="flex items-center justify-between">
                          <span className="text-xs text-gray-500">No advantage at all</span>
                          <div className="flex space-x-4">
                            {[0, 1, 2, 3, 4, 5].map((value) => (
                              <label key={value} className="cursor-pointer">
                                <input
                                  type="radio"
                                  name="answer"
                                  value={value}
                                  checked={selectedValue === value}
                                  onChange={() => setSelectedValue(value)}
                                  className="w-5 h-5 text-red-600 focus:ring-red-500"
                                />
                              </label>
                            ))}
                          </div>
                          <span className="text-xs text-gray-500">Great advantage</span>
                        </div>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>

              {/* Save Button */}
              <button
                onClick={handleSave}
                disabled={selectedValue === null || saving}
                className="px-6 py-2 bg-blue-900 text-white rounded-lg hover:bg-blue-800 transition disabled:bg-gray-300 disabled:cursor-not-allowed flex items-center space-x-2"
              >
                <span>Save</span>
                <span>💾</span>
              </button>

              {/* Rule of Thumb */}
              {question.rule_of_thumb && (
                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-900 mb-2">Rule of thumb</h3>
                  <p className="text-sm text-gray-700">{question.rule_of_thumb}</p>
                </div>
              )}

              {/* Consider */}
              {question.considerations && (
                <div className="bg-gray-50 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-900 mb-2">Consider</h3>
                  {Array.isArray(question.considerations) ? (
                    <ul className="list-disc list-inside space-y-1 text-sm text-gray-700">
                      {question.considerations.map((item, index) => (
                        <li key={index}>{item}</li>
                      ))}
                    </ul>
                  ) : (
                    <p className="text-sm text-gray-700">{question.considerations}</p>
                  )}
                </div>
              )}
            </div>

            {/* Right: Related Task and Comments */}
            <div className="space-y-6">
              {/* Related Task */}
              {question.related_task && (
                <div className="bg-white border border-gray-200 rounded-lg p-4">
                  <h3 className="font-semibold text-gray-900 mb-3">Related task</h3>
                  <div className="space-y-3">
                    <div className="flex items-start space-x-3">
                      <input type="checkbox" className="mt-1" />
                      <div className="flex-1">
                        <p className="text-sm text-gray-900">{question.related_task}</p>
                        <div className="flex items-center space-x-2 mt-2">
                          <span className="text-xs text-gray-500">May 13, 2026</span>
                          <span className="px-2 py-1 bg-gray-100 text-xs text-gray-600 rounded">
                            To do
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Comments */}
              <div className="bg-white border border-gray-200 rounded-lg p-4">
                <h3 className="font-semibold text-gray-900 mb-3">Comments</h3>
                <div className="relative">
                  <textarea
                    value={comments}
                    onChange={(e) => setComments(e.target.value)}
                    placeholder="Write comments here..."
                    rows={4}
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent resize-none text-sm"
                  />
                  <button
                    onClick={() => {
                      // Save comment
                    }}
                    className="absolute bottom-3 right-3 text-blue-600 hover:text-blue-700"
                  >
                    <Send size={18} />
                  </button>
                </div>
                <button className="text-sm text-blue-600 hover:text-blue-700 mt-2">
                  Question not relevant?
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default QuestionModal;
