import React, { useState, useEffect } from 'react';
import { Calculator, TrendingUp, Download, Save, Info, AlertCircle } from 'lucide-react';
import api from '../services/api';

const ValuationCalculator = () => {
  const [businessProfile, setBusinessProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [calculating, setCalculating] = useState(false);
  const [downloading, setDownloading] = useState(false);
  const [saving, setSaving] = useState(false);
  const [valuation, setValuation] = useState(null);

  const [inputs, setInputs] = useState({
    revenue: '',
    ebitda: '',
    assets: '',
    liabilities: '',
    industry: 'Software'
  });

  useEffect(() => {
    loadBusinessProfile();
  }, []);

  const loadBusinessProfile = async () => {
    try {
      const response = await api.get('/api/business/profile');
      if (response.data) {
        setBusinessProfile(response.data);
        // Pre-fill with business profile data if available
        if (response.data.revenue) {
          setInputs({
            revenue: response.data.revenue,
            ebitda: response.data.ebitda || '',
            assets: response.data.total_assets || '',
            liabilities: response.data.total_liabilities || '',
            industry: response.data.industry || 'Software'
          });
        }
      }
    } catch (error) {
      console.error('Error loading business profile:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCalculate = async (e) => {
    e.preventDefault();
    setCalculating(true);

    try {
      const response = await api.post('/api/valuation/calculate', {
        revenue: parseFloat(inputs.revenue) || 0,
        ebitda: parseFloat(inputs.ebitda) || 0,
        assets: parseFloat(inputs.assets) || 0,
        liabilities: parseFloat(inputs.liabilities) || 0,
        industry: inputs.industry
      });

      if (response.data) {
        setValuation(response.data);
      }
    } catch (error) {
      console.error('Error calculating valuation:', error);
      alert('Failed to calculate valuation');
    } finally {
      setCalculating(false);
    }
  };

  const handleDownloadPDF = async () => {
    if (!valuation) return;

    setDownloading(true);
    try {
      const response = await api.post('/api/pdf/basic-valuation', {
        valuation_data: {
          estimated_value: valuation.estimated_value,
          value_range_low: valuation.value_range?.low || valuation.estimated_value * 0.8,
          value_range_high: valuation.value_range?.high || valuation.estimated_value * 1.2,
          method_used: valuation.method_used || 'Industry Multiple Method',
          multiple_used: valuation.multiple_applied || 'N/A'
        },
        business_profile: {
          business_name: businessProfile?.business_name || 'Your Business',
          industry: inputs.industry,
          revenue: inputs.revenue,
          ebitda: inputs.ebitda
        }
      }, {
        responseType: 'blob'
      });

      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${businessProfile?.business_name || 'Business'}_Quick_Valuation.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();

      alert('PDF downloaded successfully!');
    } catch (error) {
      console.error('Error downloading PDF:', error);
      alert('Failed to download PDF. Please make sure your backend is running.');
    } finally {
      setDownloading(false);
    }
  };

  const handleSaveToDashboard = async () => {
    if (!valuation || !businessProfile) {
      alert('Please ensure you have a business profile and calculated valuation');
      return;
    }

    setSaving(true);
    try {
      const response = await api.post('/api/pdf/save-to-dashboard', {
        business_id: businessProfile.id,
        valuation_type: 'simple',
        estimated_value: valuation.estimated_value,
        method_used: valuation.method_used || 'Quick Valuation Method',
        calculation_details: {
          revenue: inputs.revenue,
          ebitda: inputs.ebitda,
          industry: inputs.industry,
          multiple_applied: valuation.multiple_applied,
          value_range: valuation.value_range
        }
      });

      if (response.data) {
        alert('Valuation saved to dashboard successfully!');
      }
    } catch (error) {
      console.error('Error saving to dashboard:', error);
      alert('Failed to save to dashboard. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto p-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2 flex items-center">
          <Calculator className="mr-3 text-blue-600" size={32} />
          Quick Business Valuation
        </h1>
        <p className="text-gray-600">Get an instant estimate of your business value</p>
      </div>

      {/* Info Banner */}
      <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
        <div className="flex items-start">
          <Info className="text-blue-600 mr-3 flex-shrink-0 mt-1" size={20} />
          <div className="text-sm text-blue-800">
            <p className="font-semibold mb-1">Quick Valuation Tool</p>
            <p>This tool provides a preliminary valuation estimate based on industry standard multiples. For a comprehensive analysis, try our Advanced Valuation Calculator.</p>
          </div>
        </div>
      </div>

      {/* Input Form */}
      <form onSubmit={handleCalculate} className="bg-white border rounded-lg p-6 shadow-sm mb-6">
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-2">
              Industry
            </label>
            <select
              value={inputs.industry}
              onChange={(e) => setInputs({...inputs, industry: e.target.value})}
              className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            >
              <option value="Software">Software / SaaS</option>
              <option value="Healthcare">Healthcare Services</option>
              <option value="Manufacturing">Manufacturing</option>
              <option value="Retail">Retail / E-commerce</option>
              <option value="Professional Services">Professional Services</option>
              <option value="Construction">Construction</option>
              <option value="Food & Beverage">Food & Beverage</option>
              <option value="Transportation">Transportation / Logistics</option>
              <option value="Real Estate">Real Estate Services</option>
              <option value="Other">Other</option>
            </select>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Annual Revenue ($)
              </label>
              <input
                type="number"
                value={inputs.revenue}
                onChange={(e) => setInputs({...inputs, revenue: e.target.value})}
                placeholder="e.g., 5000000"
                required
                className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                EBITDA / Profit ($)
              </label>
              <input
                type="number"
                value={inputs.ebitda}
                onChange={(e) => setInputs({...inputs, ebitda: e.target.value})}
                placeholder="e.g., 1000000"
                className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Total Assets ($) <span className="text-gray-400 font-normal">(Optional)</span>
              </label>
              <input
                type="number"
                value={inputs.assets}
                onChange={(e) => setInputs({...inputs, assets: e.target.value})}
                placeholder="e.g., 2000000"
                className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>

            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-2">
                Total Liabilities ($) <span className="text-gray-400 font-normal">(Optional)</span>
              </label>
              <input
                type="number"
                value={inputs.liabilities}
                onChange={(e) => setInputs({...inputs, liabilities: e.target.value})}
                placeholder="e.g., 500000"
                className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          <button
            type="submit"
            disabled={calculating}
            className="w-full py-3 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center justify-center"
          >
            {calculating ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                Calculating...
              </>
            ) : (
              <>
                <TrendingUp className="mr-2" size={20} />
                Calculate Valuation
              </>
            )}
          </button>
        </div>
      </form>

      {/* Results */}
      {valuation && (
        <div className="space-y-6">
          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row justify-end gap-3">
            <button
              onClick={handleDownloadPDF}
              disabled={downloading}
              className="px-4 py-2 bg-green-600 text-white rounded-lg font-semibold hover:bg-green-700 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center justify-center"
            >
              {downloading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Downloading...
                </>
              ) : (
                <>
                  <Download size={18} className="mr-2" />
                  Download PDF
                </>
              )}
            </button>

            <button
              onClick={handleSaveToDashboard}
              disabled={saving || !businessProfile}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg font-semibold hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed flex items-center justify-center"
            >
              {saving ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Saving...
                </>
              ) : (
                <>
                  <Save size={18} className="mr-2" />
                  Save to Dashboard
                </>
              )}
            </button>
          </div>

          {/* Main Value Card */}
          <div className="bg-gradient-to-r from-blue-600 to-blue-700 rounded-lg p-8 text-white shadow-lg">
            <h2 className="text-xl font-semibold mb-3">Estimated Business Value</h2>
            <p className="text-5xl font-bold mb-4">
              ${valuation.estimated_value?.toLocaleString() || '0'}
            </p>
            <p className="text-blue-100 text-sm">
              Based on {inputs.industry} industry standards
            </p>
          </div>

          {/* Value Range */}
          {valuation.value_range && (
            <div className="bg-white border rounded-lg p-6 shadow-sm">
              <h3 className="text-lg font-semibold mb-4">Valuation Range</h3>
              <div className="flex flex-col sm:flex-row justify-between items-center gap-4">
                <div className="text-center flex-1">
                  <p className="text-sm text-gray-600 mb-2">Low Estimate</p>
                  <p className="text-2xl font-bold text-gray-900">
                    ${valuation.value_range.low?.toLocaleString() || '0'}
                  </p>
                </div>
                <div className="text-center flex-1 sm:border-x px-4">
                  <p className="text-sm text-gray-600 mb-2">Mid Estimate</p>
                  <p className="text-3xl font-bold text-blue-600">
                    ${valuation.estimated_value?.toLocaleString() || '0'}
                  </p>
                </div>
                <div className="text-center flex-1">
                  <p className="text-sm text-gray-600 mb-2">High Estimate</p>
                  <p className="text-2xl font-bold text-gray-900">
                    ${valuation.value_range.high?.toLocaleString() || '0'}
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Method Details */}
          <div className="bg-white border rounded-lg p-6 shadow-sm">
            <h3 className="text-lg font-semibold mb-4">Calculation Details</h3>
            <div className="space-y-3 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Method Used:</span>
                <span className="font-semibold text-gray-900">{valuation.method_used || 'Industry Multiple'}</span>
              </div>
              {valuation.multiple_applied && (
                <div className="flex justify-between">
                  <span className="text-gray-600">Multiple Applied:</span>
                  <span className="font-semibold text-gray-900">{valuation.multiple_applied}</span>
                </div>
              )}
              {valuation.ebitda_margin && (
                <div className="flex justify-between">
                  <span className="text-gray-600">EBITDA Margin:</span>
                  <span className="font-semibold text-gray-900">{valuation.ebitda_margin}%</span>
                </div>
              )}
            </div>
          </div>

          {/* Disclaimer */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-start">
              <AlertCircle className="text-yellow-600 mr-3 flex-shrink-0 mt-1" size={20} />
              <div className="text-sm text-yellow-800">
                <p className="font-semibold mb-1">Important Notice</p>
                <p>This is a preliminary estimate for planning purposes only. For accurate valuations, please consult with a Certified Exit Planning Advisor (CEPA) or business valuation professional. Actual transaction values may vary significantly based on market conditions, buyer motivations, and other factors.</p>
              </div>
            </div>
          </div>

          {/* Next Steps */}
          <div className="bg-white border rounded-lg p-6 shadow-sm">
            <h3 className="text-lg font-semibold mb-4">Next Steps</h3>
            <ul className="space-y-3 text-sm">
              <li className="flex items-start">
                <span className="text-blue-600 mr-2"></span>
                <span>Try our <span className="font-semibold">Advanced Valuation Calculator</span> for multi-method analysis</span>
              </li>
              <li className="flex items-start">
                <span className="text-blue-600 mr-2"></span>
                <span>Complete the <span className="font-semibold">Exit Readiness Assessment</span> to identify value gaps</span>
              </li>
              <li className="flex items-start">
                <span className="text-blue-600 mr-2"></span>
                <span>Connect with a <span className="font-semibold">Certified Exit Planning Advisor</span> for personalized guidance</span>
              </li>
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default ValuationCalculator;
