import React, { useState, useEffect } from 'react';
import { FileText, PlayCircle } from 'lucide-react';
import { getAssessment, startAssessment } from '../services/api';
import AssessmentModal from '../components/AssessmentModal';
import CategoryDetailModal from '../components/CategoryDetailModal';
import './GapAnalysis.css';

const GapAnalysis = () => {
  const [assessment, setAssessment] = useState(null);
  const [allQuestions, setAllQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showAssessmentModal, setShowAssessmentModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);

  useEffect(() => {
    loadAssessment();
    loadAllQuestions();
  }, []);

  const loadAssessment = async () => {
    setLoading(true);
    try {
      const result = await getAssessment();

      if (result.success) {
        setAssessment(result.data);
      } else {
        // No assessment exists, start one
        const startResult = await startAssessment();
        if (startResult.success) {
          const newResult = await getAssessment();
          setAssessment(newResult.data);
        }
      }
    } catch (error) {
      console.error('Error loading assessment:', error);
    }
    setLoading(false);
  };

  const loadAllQuestions = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/assessment/questions', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const data = await response.json();
        setAllQuestions(data.questions || []);
      }
    } catch (error) {
      console.error('Error loading questions:', error);
    }
  };

  const handleModalSave = async () => {
    // Reload assessment to update scores
    await loadAssessment();
  };

  // Get all answered questions from assessment
  const getAnsweredQuestions = () => {
    if (!assessment || !assessment.categories) return [];

    const answered = [];
    Object.keys(assessment.categories).forEach(catKey => {
      const category = assessment.categories[catKey];
      Object.keys(category.subjects || {}).forEach(subjectKey => {
        const subject = category.subjects[subjectKey];
        if (subject.questions) {
          answered.push(...subject.questions);
        }
      });
    });

    return answered;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-gray-600">Loading assessment...</div>
      </div>
    );
  }

  if (!assessment) {
    return (
      <div className="flex items-center justify-center h-screen">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">No Assessment Found</h2>
          <button
            onClick={async () => {
              await startAssessment();
              loadAssessment();
            }}
            className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Start Assessment
          </button>
        </div>
      </div>
    );
  }

  const categories = assessment.categories || {};
  const answeredQuestions = getAnsweredQuestions();
  const progressPercent = allQuestions.length > 0
    ? (assessment.answered_questions / allQuestions.length) * 100
    : 0;

  // Prepare data for donut chart
  const donutData = Object.keys(categories).map(key => ({
    name: categories[key].name,
    value: categories[key].score || 0
  })).filter(item => item.value > 0);

  const donutTotal = donutData.reduce((sum, item) => sum + item.value, 0);

  const colors = [
    '#dc2626', '#ea580c', '#facc15', '#84cc16',
    '#22c55e', '#06b6d4', '#3b82f6', '#8b5cf6'
  ];

  return (
    <div className="max-w-7xl mx-auto p-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Gap Analysis</h1>
        <p className="text-gray-600">
          Complete the assessment to identify areas for improvement before exit
        </p>
      </div>

      {/* Start/Continue Assessment Card */}
      <div className="bg-gradient-to-r from-blue-50 to-blue-100 border-2 border-blue-200 rounded-lg p-8 mb-8">
        <div className="flex items-center justify-between">
          <div className="flex-1">
            <h2 className="text-2xl font-bold text-gray-900 mb-2">
              {assessment.answered_questions === 0 ? 'Ready to Begin?' : 'Continue Your Assessment'}
            </h2>
            <p className="text-gray-700 mb-4">
              {assessment.answered_questions === 0 
                ? 'Answer 21 questions across 8 categories to assess your exit readiness'
                : `You've answered ${assessment.answered_questions} of ${allQuestions.length} questions. Keep going!`
              }
            </p>
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setShowAssessmentModal(true)}
                className="px-8 py-4 bg-blue-600 text-white rounded-lg hover:bg-blue-700 text-lg font-semibold flex items-center space-x-2"
              >
                <PlayCircle size={24} />
                <span>{assessment.answered_questions === 0 ? 'Start Assessment' : 'Continue Assessment'}</span>
              </button>
              {assessment.answered_questions > 0 && (
                <div className="flex-1 max-w-xs">
                  <div className="flex justify-between text-sm text-gray-700 mb-1">
                    <span>Progress</span>
                    <span>{Math.round(progressPercent)}%</span>
                  </div>
                  <div className="w-full bg-blue-200 rounded-full h-3">
                    <div
                      className="bg-blue-600 h-3 rounded-full transition-all"
                      style={{ width: `${progressPercent}%` }}
                    ></div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Results Grid - Only show if some questions answered */}
      {assessment.answered_questions > 0 && (
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Scores */}
          <div className="lg:col-span-2 space-y-6">
            {/* Overall Score Card */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h2 className="text-xl font-bold text-gray-900 mb-6">Your Scores</h2>

              <div className="grid grid-cols-3 gap-8 mb-6">
                <div>
                  <p className="text-sm text-gray-500 mb-1">Initial score</p>
                  <p className="text-3xl font-bold text-gray-400">
                    {Math.round(assessment.initial_score)}%
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-500 mb-1">Current score</p>
                  <p className="text-3xl font-bold text-gray-900">
                    {Math.round(assessment.overall_score)}%
                  </p>
                </div>
                <div>
                  <p className="text-sm text-gray-500 mb-1">Questions</p>
                  <p className="text-3xl font-bold text-blue-600">
                    {assessment.answered_questions}/{allQuestions.length}
                  </p>
                </div>
              </div>

              {/* Category Scores */}
              <div className="space-y-3">
                <div className="flex items-center justify-between mb-3">
                  <h3 className="font-semibold text-gray-800">Category Breakdown</h3>
                  <span className="text-xs text-gray-500">Click to view details</span>
                </div>
                {Object.keys(categories).map((catKey) => {
                  const category = categories[catKey];
                  const score = category.score || 0;

                  return (
                    <div 
                      key={catKey} 
                      className="flex items-center space-x-3 cursor-pointer hover:bg-gray-50 p-2 rounded transition"
                      onClick={() => setSelectedCategory({ key: catKey, data: category })}
                    >
                      <div className="flex-1">
                        <div className="flex items-center justify-between mb-1">
                          <span className="text-sm font-medium text-gray-700 hover:text-blue-600">
                            {category.name}
                          </span>
                          <span className="text-sm font-semibold text-gray-900">{Math.round(score)}%</span>
                        </div>
                        <div className="w-full bg-gray-200 rounded-full h-2">
                          <div
                            className={`h-2 rounded-full transition-all ${
                              score < 30
                                ? 'bg-red-500'
                                : score < 60
                                ? 'bg-yellow-500'
                                : 'bg-green-500'
                            }`}
                            style={{ width: `${score}%` }}
                          ></div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Generate PDF Button */}
            <button className="flex items-center space-x-2 px-6 py-3 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 transition">
              <FileText size={20} />
              <span>Generate PDF Report</span>
            </button>
          </div>

          {/* Right Column - Charts */}
          <div className="space-y-6">
            {/* Donut Chart */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">Category Distribution</h3>
              <div className="flex items-center justify-center">
                {donutData.length > 0 && donutTotal > 0 ? (
                  <div className="relative w-64 h-64">
                    <svg viewBox="0 0 200 200" className="transform -rotate-90">
                      {donutData.map((item, index) => {
                        const percentage = item.value / donutTotal;
                        const startAngle = donutData
                          .slice(0, index)
                          .reduce((sum, d) => sum + (d.value / donutTotal), 0) * 360;
                        const endAngle = startAngle + percentage * 360;

                        const largeArc = endAngle - startAngle > 180 ? 1 : 0;
                        const startX = 100 + 80 * Math.cos((startAngle * Math.PI) / 180);
                        const startY = 100 + 80 * Math.sin((startAngle * Math.PI) / 180);
                        const endX = 100 + 80 * Math.cos((endAngle * Math.PI) / 180);
                        const endY = 100 + 80 * Math.sin((endAngle * Math.PI) / 180);

                        return (
                          <path
                            key={index}
                            d={`M 100 100 L ${startX} ${startY} A 80 80 0 ${largeArc} 1 ${endX} ${endY} Z`}
                            fill={colors[index % colors.length]}
                            opacity="0.8"
                          />
                        );
                      })}
                      <circle cx="100" cy="100" r="50" fill="white" />
                    </svg>
                    <div className="absolute inset-0 flex items-center justify-center">
                      <div className="text-center">
                        <div className="text-4xl font-bold text-blue-900">
                          {Math.round(assessment.overall_score)}
                        </div>
                      </div>
                    </div>
                  </div>
                ) : (
                  <div className="text-center py-12">
                    <p className="text-gray-600">Complete more questions</p>
                    <p className="text-sm text-gray-500">to see your distribution</p>
                  </div>
                )}
              </div>
            </div>

            {/* Readiness vs Attractiveness Chart */}
            <div className="bg-white rounded-lg border border-gray-200 p-6">
              <h3 className="text-lg font-bold text-gray-900 mb-4">
                Readiness & Attractiveness
              </h3>
              <div className="relative w-full h-64">
                <svg viewBox="0 0 100 100" className="w-full h-full">
                  {/* Grid */}
                  {[20, 40, 60, 80].map(val => (
                    <React.Fragment key={val}>
                      <line x1="0" y1={100-val} x2="100" y2={100-val} stroke="#e5e7eb" strokeWidth="0.5" />
                      <line x1={val} y1="0" x2={val} y2="100" stroke="#e5e7eb" strokeWidth="0.5" />
                    </React.Fragment>
                  ))}

                  {/* Axes */}
                  <line x1="0" y1="100" x2="100" y2="100" stroke="#374151" strokeWidth="1" />
                  <line x1="0" y1="0" x2="0" y2="100" stroke="#374151" strokeWidth="1" />

                  {/* Current position */}
                  {assessment.overall_score > 0 && (
                    <circle
                      cx={assessment.readiness_score || 20}
                      cy={100 - (assessment.attractiveness_score || 20)}
                      r="3"
                      fill="#1e40af"
                      stroke="white"
                      strokeWidth="1"
                    />
                  )}
                </svg>

                <div className="absolute bottom-0 left-0 right-0 text-center text-xs text-gray-600">
                  Readiness
                </div>
                <div className="absolute left-0 top-0 bottom-0 flex items-center">
                  <div className="transform -rotate-90 text-xs text-gray-600 whitespace-nowrap">
                    Attractiveness
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Assessment Modal */}
      {showAssessmentModal && (
        <AssessmentModal
          assessmentId={assessment.id || assessment.assessment_id}
          allQuestions={allQuestions}
          answeredQuestions={answeredQuestions}
          onClose={() => {
            setShowAssessmentModal(false);
            loadAssessment(); // Refresh scores when closing
          }}
          onSave={handleModalSave}
        />
      )}

      {/* Category Detail Modal */}
      {selectedCategory && (
        <CategoryDetailModal
          category={selectedCategory.data}
          categoryKey={selectedCategory.key}
          allQuestions={allQuestions}
          answeredQuestions={answeredQuestions}
          assessmentId={assessment.id || assessment.assessment_id}
          onClose={() => setSelectedCategory(null)}
          onUpdate={async () => {
            await loadAssessment();
          }}
        />
      )}
    </div>
  );
};

export default GapAnalysis;
